   668â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   669â†’    }
   670â†’
   671â†’    // Save merged settings
   672â†’    saveSettings({
   673â†’        activePreset: presetId,
   674â†’        sensitivity: settings.sensitivity,
   675â†’        threshold: settings.threshold,
