   739â†’async function runDetection() {
   740â†’    if (isOperationInProgress) return;
   741â†’
   742â†’    // Check sequence with enhanced error messaging
   743â†’    const hasSequence = await checkSequenceOpen();
   744â†’    if (!hasSequence) {
   745â†’        // Check if there was a communication error vs just no sequence
   746â†’        const lastError = window.spliceState.lastSequenceCheckError;
   747â†’        if (lastError && (lastError.includes('CEP') || lastError.includes('communication') || lastError.includes('evalScript'))) {
   748â†’            setStatus('Premiere Pro connection issue - try reopening panel', true);
   749â†’            console.error('[SPLICE] runDetection: JSX communication failure:', lastError);
   750â†’        } else {
   751â†’            setStatus('Please open a sequence in the Timeline first', true);
   752â†’            debugLog('runDetection: No active sequence in Timeline');
   753â†’        }
   754â†’        // Clear the error for next check
   755â†’        window.spliceState.lastSequenceCheckError = null;
   756â†’        return;
   757â†’    }
   758â†’
   759â†’    // Check online
   760â†’    if (!isOnline()) {
   761â†’        setStatus('Offline - Check your connection', true);
   762â†’        return;
   763â†’    }
   764â†’
   765â†’    isOperationInProgress = true;
   766â†’    ui.goBtn.disabled = true;
   767â†’    showProgress('Detecting silences...');
   768â†’
   769â†’    try {
   770â†’        const settings = getSettings();
   771â†’
   772â†’        // Get sequence info
   773â†’        const seqInfo = await jsx.call('getActiveSequence');
   774â†’        debugLog('Sequence info:', seqInfo);
   775â†’
   776â†’        // Store sequence info for later use
   777â†’        currentSequenceInfo = seqInfo;
   778â†’        window.spliceState.activeSequence = seqInfo;
   779â†’        if (seqInfo && seqInfo.videoPath) {
   780â†’            window.spliceState.currentVideoPath = seqInfo.videoPath;
   781â†’        }
   782â†’
   783â†’        // Export audio via JSX for silence detection
   784â†’        updateProgress(10, 'Exporting audio...');
   785â†’
   786â†’        let audioFilePath = null;
   787â†’        try {
   788â†’            // FIX: CEP-CRIT-010 - Main.js Export Timeout
   789â†’            // Use LONG timeout (120s) for export operations
   790â†’            const exportResult = await jsx.callWithTimeout(
   791â†’                'exportSequenceAudioForAnalysis',
   792â†’                [],
   793â†’                JSX_TIMEOUTS.LONG
   794â†’            );
   795â†’            if (exportResult && exportResult.success && exportResult.outputPath) {
   796â†’                audioFilePath = exportResult.outputPath;
   797â†’                debugLog('Audio exported to:', audioFilePath);
   798â†’            } else if (exportResult && exportResult.error) {
   799â†’                throw new Error(exportResult.error);
   800â†’            }
   801â†’        } catch (exportError) {
   802â†’            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   803â†’            // Try to get audio from first clip in timeline
   804â†’            try {
   805â†’                const clipInfo = await jsx.call('getFirstClipAudioPath');
   806â†’                if (clipInfo && clipInfo.path) {
   807â†’                    audioFilePath = clipInfo.path;
   808â†’                    debugLog('Using clip audio path:', audioFilePath);
   809â†’                }
   810â†’            } catch (clipError) {
   811â†’                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   812â†’            }
   813â†’        }
   814â†’
   815â†’        if (!audioFilePath) {
   816â†’            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   817â†’        }
   818â†’
   819â†’        // Store audio path globally for other modules
   820â†’        currentAudioPath = audioFilePath;
   821â†’        window.spliceState.audioPath = audioFilePath;
   822â†’
   823â†’        // Call backend for silence detection with audio file path
   824â†’        updateProgress(30, 'Detecting silences...');
   825â†’        const silenceResponse = await fetchWithTimeout(
   826â†’            `${getBackendUrl()}/silences-rms`,
   827â†’            {
   828â†’                method: 'POST',
   829â†’                headers: getAuthHeaders(),
   830â†’                body: JSON.stringify({
   831â†’                    wavPath: audioFilePath,
   832â†’                    sensitivity: settings.sensitivity,
   833â†’                    minSilenceLength: settings.minSilenceLength || 0.5
   834â†’                })
   835â†’            }
   836â†’        );
   837â†’
   838â†’        if (!silenceResponse.ok) {
   839â†’            throw new Error(await parseErrorResponse(silenceResponse));
   840â†’        }
   841â†’
   842â†’        const silenceData = await silenceResponse.json();
   843â†’        previewSilences = silenceData.silences || [];
   844â†’
   845â†’        // Detect takes if enabled
   846â†’        if (settings.enableTakesDetection) {
   847â†’            updateProgress(60, 'Detecting takes...');
   848â†’            try {
   849â†’                const analyzeResponse = await fetchWithTimeout(
   850â†’                    `${getBackendUrl()}/analyze`,
   851â†’                    {
   852â†’                        method: 'POST',
   853â†’                        headers: getAuthHeaders(),
   854â†’                        body: JSON.stringify({
   855â†’                            wavPath: audioFilePath,
   856â†’                            detectTakes: true
   857â†’                        })
   858â†’                    }
   859â†’                );
   860â†’
   861â†’                if (analyzeResponse.ok) {
   862â†’                    const analyzeData = await analyzeResponse.json();
   863â†’                    previewTakes = analyzeData.takes || [];
   864â†’
   865â†’                    // Store transcript for captions/music modules
   866â†’                    if (analyzeData.transcript) {
   867â†’                        currentTranscript = analyzeData.transcript;
   868â†’                        window.currentTranscript = analyzeData.transcript;
   869â†’                        window.spliceState.lastTranscript = analyzeData.transcript;
   870â†’                    }
   871â†’                } else {
   872â†’                    // Handle specific error codes
   873â†’                    const errorMsg = await parseErrorResponse(analyzeResponse);
   874â†’                    if (analyzeResponse.status === 402) {
   875â†’                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   876â†’                        setStatus('Takes detection skipped: insufficient credits', true);
   877â†’                    } else if (analyzeResponse.status === 401) {
