  1504â†’/**
  1505â†’ * ACCESSIBILITY: Announce message to screen readers via ARIA live region
  1506â†’ * @param {string} message - Message to announce
  1507â†’ * @param {string} priority - 'polite' or 'assertive'
  1508â†’ */
  1509â†’function announceToScreenReader(message, priority = 'polite') {
  1510â†’    // Use existing status element or create temporary one
  1511â†’    const statusEl = document.getElementById('status');
  1512â†’    if (statusEl && priority === 'polite') {
  1513â†’        const originalText = statusEl.textContent;
  1514â†’        statusEl.textContent = message;
  1515â†’        // Restore after announcement
  1516â†’        setTimeout(() => {
  1517â†’            if (statusEl.textContent === message) {
  1518â†’                statusEl.textContent = originalText;
  1519â†’            }
  1520â†’        }, 1000);
  1521â†’        return;
  1522â†’    }
  1523â†’
  1524â†’    // Create temporary live region for assertive announcements
  1525â†’    const liveRegion = document.createElement('div');
  1526â†’    liveRegion.setAttribute('role', 'alert');
  1527â†’    liveRegion.setAttribute('aria-live', priority);
  1528â†’    liveRegion.setAttribute('aria-atomic', 'true');
  1529â†’    liveRegion.className = 'sr-only';
  1530â†’    liveRegion.style.cssText = 'position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);border:0;';
  1531â†’    liveRegion.textContent = message;
  1532â†’    document.body.appendChild(liveRegion);
  1533â†’
  1534â†’    // Remove after announcement
  1535â†’    setTimeout(() => liveRegion.remove(), 1000);
  1536â†’}
