  1577â†’async function activateLicense() {
  1578â†’    if (!ui.licenseKeyInput) return;
  1579â†’
  1580â†’    const licenseKey = ui.licenseKeyInput.value.trim();
  1581â†’    if (!licenseKey) {
  1582â†’        setStatus('Please enter a license key', true);
  1583â†’        return;
  1584â†’    }
  1585â†’
  1586â†’    try {
  1587â†’        const response = await fetchWithTimeout(
  1588â†’            `${getBackendUrl()}/license/activate`,
  1589â†’            {
  1590â†’                method: 'POST',
  1591â†’                headers: { 'Content-Type': 'application/json' },
  1592â†’                body: JSON.stringify({ key: licenseKey })
  1593â†’            }
  1594â†’        );
  1595â†’
  1596â†’        if (!response.ok) {
  1597â†’            throw new Error(await parseErrorResponse(response));
  1598â†’        }
  1599â†’
  1600â†’        const data = await response.json();
  1601â†’        saveSettings({
  1602â†’            customerId: data.customerId,
  1603â†’            licenseKey: licenseKey
  1604â†’        });
  1605â†’
  1606â†’        if (ui.loginModal) ui.loginModal.classList.add('hidden');
  1607â†’        setStatus('License activated');
  1608â†’        updateCredits();
  1609â†’    } catch (error) {
  1610â†’        setStatus('Activation failed: ' + error.message, true);
  1611â†’    }
  1612â†’}
