// PREVIEW HANDLERS
// ============================================================================
function initPreviewHandlers() {
    // Select all checkbox
    if (ui.selectAllSilences) {
        ui.selectAllSilences.addEventListener('change', () => {
            if (ui.selectAllSilences.checked) {
                previewSilences.forEach((_, i) => selectedSilenceIndices.add(i));
