   160â†’    speakerItem.dataset.speakerIndex = newIndex;
   161â†’    speakerItem.innerHTML = `
   162â†’        <input type="text" class="speaker-name" value="${escapeHtml(defaultName)}" placeholder="Name" aria-label="Speaker ${newIndex + 1} name">
   163â†’        <select class="speaker-track" aria-label="Speaker ${newIndex + 1} video track">
   164â†’            <option value="0" ${newIndex === 0 ? 'selected' : ''}>V1</option>
   165â†’            <option value="1" ${newIndex === 1 ? 'selected' : ''}>V2</option>
   166â†’            <option value="2" ${newIndex === 2 ? 'selected' : ''}>V3</option>
