   187â†’    const item = multitrackUI.speakerList.querySelector(`[data-speaker-index="${index}"]`);
   188â†’    if (item) {
   189â†’        item.remove();
   190â†’    }
   191â†’
   192â†’    // Re-index remaining speakers
   193â†’    reindexSpeakers();
   194â†’
   195â†’    // Update remove button visibility
   196â†’    updateRemoveButtonVisibility();
