   298â†’async function getAudioPath() {
   299â†’    // FIX: CEP-CRIT-013 - Multitrack JSX Error Handling
   300â†’    try {
   301â†’        // Try to get audio path from active sequence (QUICK timeout)
   302â†’        const seqInfo = await jsx.callWithTimeout('getActiveSequence', [], JSX_TIMEOUTS.QUICK);
   303â†’        if (seqInfo && seqInfo.audioPath) {
   304â†’            return seqInfo.audioPath;
   305â†’        }
   306â†’
   307â†’        // Fallback: export sequence audio for analysis (LONG timeout)
   308â†’        const exportResult = await jsx.callWithTimeout(
   309â†’            'exportSequenceAudioForAnalysis',
   310â†’            [],
   311â†’            JSX_TIMEOUTS.LONG
   312â†’        );
   313â†’        if (exportResult && exportResult.path) {
   314â†’            return exportResult.path;
   315â†’        }
   316â†’
   317â†’        // Last resort: get first clip audio path (QUICK timeout)
   318â†’        const clipPath = await jsx.callWithTimeout('getFirstClipAudioPath', [], JSX_TIMEOUTS.QUICK);
   319â†’        if (clipPath) {
   320â†’            return clipPath;
   321â†’        }
   322â†’
   323â†’        throw new Error('Could not determine audio path from any source');
   324â†’    } catch (e) {
   325â†’        console.error('[SPLICE Multitrack] Failed to get audio path:', e);
   326â†’
   327â†’        // Provide actionable error message
   328â†’        if (e.code === 'JSX_TIMEOUT') {
   329â†’            throw new Error('Premiere Pro is not responding. Try closing other apps or restarting Premiere.');
   330â†’        } else if (e.message.includes('no sequence')) {
   331â†’            throw new Error('Please open a sequence in Premiere Pro first.');
   332â†’        } else {
   333â†’            throw new Error(`Audio export failed: ${e.message}`);
   334â†’        }
   335â†’    }
   336â†’}
