   341â†’async function analyzeMultitrack() {
   342â†’    if (isMultitrackOperationInProgress) return;
   343â†’
   344â†’    // FIX: CEP-CRIT-012 - Multitrack Race Condition
   345â†’    // Set flag IMMEDIATELY to prevent double operations
   346â†’    isMultitrackOperationInProgress = true;
   347â†’
   348â†’    // Check tier access - Multitrack requires Team tier
   349â†’    if (typeof hasFeatureAccess === 'function' && !hasFeatureAccess('multitrack')) {
   350â†’        const requiredTier = typeof getRequiredTier === 'function' ? getRequiredTier('multitrack') : 'Team';
   351â†’        setStatus(`Multitrack auto-balance requires ${requiredTier} tier`);
   352â†’        if (typeof showUpgradeModal === 'function') {
   353â†’            showUpgradeModal('Multitrack Auto-Balance', requiredTier);
   354â†’        }
   355â†’        isMultitrackOperationInProgress = false;  // Reset flag on early return
   356â†’        return;
   357â†’    }
   358â†’
   359â†’    // Check online status
   360â†’    if (typeof isOnline === 'function' && !isOnline()) {
   361â†’        setStatus('Offline - Check your connection');
   362â†’        isMultitrackOperationInProgress = false;  // Reset flag on early return
   363â†’        return;
   364â†’    }
   365â†’    if (multitrackUI.analyzeBtn) {
   366â†’        multitrackUI.analyzeBtn.disabled = true;
   367â†’        multitrackUI.analyzeBtn.textContent = 'Analyzing...';
   368â†’    }
   369â†’
   370â†’    try {
   371â†’        setStatus('Exporting audio tracks...');
   372â†’
   373â†’        // FIX: CEP-CRIT-014 - Get sequence ID for validation
   374â†’        const seqInfo = await jsx.callWithTimeout('getActiveSequence', [], JSX_TIMEOUTS.QUICK);
   375â†’        const currentSequenceId = seqInfo?.id || seqInfo?.name || Date.now().toString();
   376â†’
   377â†’        // Get audio path
   378â†’        const audioPath = await getAudioPath();
   379â†’        if (!audioPath) {
   380â†’            throw new Error('Audio export path not initialized');
   381â†’        }
   382â†’
   383â†’        // For now, we use the main audio path
   384â†’        // In a full implementation, we'd export separate tracks per speaker
   385â†’        const audioPaths = [audioPath];
   386â†’
   387â†’        // Add additional tracks if multi-track sequence
   388â†’        if (speakerConfig.length > 1) {
   389â†’            for (let i = 1; i < speakerConfig.length; i++) {
   390â†’                audioPaths.push(audioPath);
   391â†’            }
   392â†’        }
   393â†’
   394â†’        setStatus('Analyzing speakers...');
   395â†’
   396â†’        const settings = getMultitrackSettings();
   397â†’        const apiUrl = getBackendUrl();
   398â†’
   399â†’        const response = await fetchWithTimeout(`${apiUrl}/multitrack`, {
   400â†’            method: 'POST',
   401â†’            headers: getAuthHeaders(),
   402â†’            body: JSON.stringify({
   403â†’                audioPaths,
   404â†’                speakerNames: settings.speakerNames,
   405â†’                videoTrackMapping: settings.videoTrackMapping,
   406â†’                wideShotEnabled: settings.wideShotEnabled,
   407â†’                wideShotPercentage: settings.wideShotPercentage,
   408â†’                wideShotTracks: settings.wideShotTracks,
   409â†’                minShotDuration: settings.minShotDuration,
   410â†’                switchingFrequency: settings.switchingFrequency
   411â†’            })
   412â†’        }, 120000);
   413â†’
   414â†’        if (!response.ok) {
   415â†’            const errorMsg = await parseErrorResponse(response);
   416â†’            throw new Error(errorMsg);
   417â†’        }
   418â†’
   419â†’        const data = await response.json();
   420â†’        analysisResults = data;
   421â†’        analysisSequenceId = currentSequenceId;  // FIX: CEP-CRIT-014 - Store sequence ID
   422â†’
   423â†’        // Display results
   424â†’        displayMultitrackResults(data);
   425â†’
   426â†’        // Enable apply button
   427â†’        if (multitrackUI.applyBtn) {
   428â†’            multitrackUI.applyBtn.disabled = false;
   429â†’        }
   430â†’
   431â†’        setStatus(`Analysis complete - ${data.decisions?.length || 0} switching decisions`);
   432â†’
   433â†’    } catch (err) {
   434â†’        console.error('[SPLICE Multitrack] Analysis error:', err);
   435â†’        setStatus('Analysis failed: ' + err.message, true);
   436â†’    } finally {
   437â†’        isMultitrackOperationInProgress = false;
   438â†’        if (multitrackUI.analyzeBtn) {
   439â†’            multitrackUI.analyzeBtn.disabled = false;
   440â†’            multitrackUI.analyzeBtn.textContent = 'Analyze';
   441â†’        }
   442â†’    }
   443â†’}
