   554â†’function renderDistributionChart(metadata) {
   555â†’    if (!multitrackUI.distributionChart || !metadata) return;
   556â†’
   557â†’    const speakerPercentages = metadata.speakerPercentages || {};
   558â†’    const wideShotPct = metadata.wideShotPercentage || 0;
   559â†’
   560â†’    const fragment = document.createDocumentFragment();
   561â†’
   562â†’    // Add speaker bars
   563â†’    let speakerIndex = 0;
   564â†’    for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   565â†’        if (pct > 0) {
   566â†’            const bar = document.createElement('div');
   567â†’            bar.className = `distribution-bar speaker-${speakerIndex % 4}`;
   568â†’            bar.style.width = `${pct}%`;
   569â†’            bar.textContent = pct >= 10 ? `${speaker}: ${pct.toFixed(0)}%` : '';
   570â†’            bar.title = `${speaker}: ${pct.toFixed(1)}%`;
   571â†’            fragment.appendChild(bar);
   572â†’        }
   573â†’        speakerIndex++;
   574â†’    }
   575â†’
   576â†’    // Add wide shot bar
   577â†’    if (wideShotPct > 0) {
   578â†’        const bar = document.createElement('div');
   579â†’        bar.className = 'distribution-bar wide-shot';
   580â†’        bar.style.width = `${wideShotPct}%`;
   581â†’        bar.textContent = wideShotPct >= 8 ? `Wide: ${wideShotPct.toFixed(0)}%` : '';
   582â†’        bar.title = `Wide Shot: ${wideShotPct.toFixed(1)}%`;
   583â†’        fragment.appendChild(bar);
   584â†’    }
   585â†’
   586â†’    multitrackUI.distributionChart.innerHTML = '';
   587â†’    multitrackUI.distributionChart.appendChild(fragment);
   588â†’}
