   277â†’/**
   278â†’ * Handle text changes in editor
   279â†’ */
   280â†’function handleTextChange(newText) {
   281â†’  // Save current state to undo stack
   282â†’  if (textEditorState.currentText !== newText) {
   283â†’    textEditorState.undoStack.push(textEditorState.currentText);
   284â†’    textEditorState.redoStack = []; // Clear redo stack on new edit
   285â†’
   286â†’    // Limit undo stack size
   287â†’    if (textEditorState.undoStack.length > MAX_UNDO_STACK) {
   288â†’      textEditorState.undoStack.shift();
   289â†’    }
   290â†’  }
   291â†’
   292â†’  textEditorState.currentText = newText;
   293â†’  textEditorState.isDirty = true;
   294â†’  textEditorState.previewChanges = null; // Clear preview
   295â†’
   296â†’  updateEditorStatus();
   297â†’}
   298â†’
   299â†’/**
   300â†’ * Undo last text edit
   301â†’ */
   302â†’function undoTextEdit() {
   303â†’  if (textEditorState.undoStack.length === 0) {
   304â†’    showTextEditorStatus('Nothing to undo', 'info');
   305â†’    return;
   306â†’  }
   307â†’
   308â†’  // Save current to redo stack
   309â†’  textEditorState.redoStack.push(textEditorState.currentText);
   310â†’
   311â†’  // Restore from undo stack
   312â†’  textEditorState.currentText = textEditorState.undoStack.pop();
   313â†’
   314â†’  // Update editor content
   315â†’  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   316â†’  if (editor) {
   317â†’    editor.value = textEditorState.currentText;
   318â†’  }
   319â†’
   320â†’  updateEditorStatus();
   321â†’  showTextEditorStatus('Undo applied', 'info');
   322â†’}
   323â†’
   324â†’/**
   325â†’ * Redo last undone edit
   326â†’ */
   327â†’function redoTextEdit() {
   328â†’  if (textEditorState.redoStack.length === 0) {
   329â†’    showTextEditorStatus('Nothing to redo', 'info');
   330â†’    return;
   331â†’  }
   332â†’
   333â†’  // Save current to undo stack
   334â†’  textEditorState.undoStack.push(textEditorState.currentText);
   335â†’
   336â†’  // Restore from redo stack
   337â†’  textEditorState.currentText = textEditorState.redoStack.pop();
   338â†’
   339â†’  // Update editor content
   340â†’  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   341â†’  if (editor) {
   342â†’    editor.value = textEditorState.currentText;
   343â†’  }
   344â†’
   345â†’  updateEditorStatus();
   346â†’  showTextEditorStatus('Redo applied', 'info');
   347â†’}
