   457â†’/**
   458â†’ * Apply text edits and generate cut list
   459â†’ */
   460â†’async function applyTextEdits() {
   461â†’  if (!textEditorState.editableTranscript || !textEditorState.isDirty) {
   462â†’    showTextEditorStatus('No changes to apply', 'info');
   463â†’    return;
   464â†’  }
   465â†’
   466â†’  textEditorState.isProcessing = true;
   467â†’  updateProcessingState(true);
   468â†’  showTextEditorStatus('Applying edits...', 'info');
   469â†’
   470â†’  try {
   471â†’    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   472â†’    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   473â†’    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   474â†’
   475â†’    // Apply edits
   476â†’    const applyResponse = await fetchFn(`${backendUrl}/text-edit/apply`, {
   477â†’      method: 'POST',
   478â†’      headers,
   479â†’      body: JSON.stringify({
   480â†’        transcript: textEditorState.editableTranscript,
   481â†’        editedText: textEditorState.currentText
   482â†’      })
   483â†’    });
   484â†’
   485â†’    if (!applyResponse.ok) {
   486â†’      const err = await applyResponse.json();
   487â†’      throw new Error(err.error || 'Apply failed');
   488â†’    }
   489â†’
   490â†’    const applyData = await applyResponse.json();
   491â†’
   492â†’    // Generate cut list from operations
   493â†’    const cutListResponse = await fetchFn(`${backendUrl}/text-edit/cut-list`, {
   494â†’      method: 'POST',
   495â†’      headers,
   496â†’      body: JSON.stringify({
   497â†’        operations: applyData.operations,
   498â†’        sourceInfo: {
   499â†’          sourceName: textEditorState.originalTranscript?.source?.name || 'source',
   500â†’          duration: textEditorState.editableTranscript.duration
   501â†’        }
   502â†’      })
   503â†’    });
   504â†’
   505â†’    if (!cutListResponse.ok) {
   506â†’      const err = await cutListResponse.json();
   507â†’      throw new Error(err.error || 'Cut list generation failed');
   508â†’    }
   509â†’
   510â†’    const cutListData = await cutListResponse.json();
   511â†’
   512â†’    // Validate cut list before storing
   513â†’    if (!cutListData.cutList) {
   514â†’      throw new Error('Backend did not return a cut list');
   515â†’    }
   516â†’    if (!cutListData.cutList.segments || !Array.isArray(cutListData.cutList.segments)) {
   517â†’      throw new Error('Invalid cut list format: segments missing or not an array');
   518â†’    }
   519â†’    if (cutListData.cutList.segments.length === 0) {
   520â†’      throw new Error('Cut list is empty. No segments to build. Try making smaller edits.');
   521â†’    }
   522â†’
   523â†’    // Validate each segment has required fields
   524â†’    for (let i = 0; i < cutListData.cutList.segments.length; i++) {
   525â†’      const seg = cutListData.cutList.segments[i];
   526â†’      if (typeof seg.inPoint !== 'number' || typeof seg.outPoint !== 'number') {
   527â†’        throw new Error(`Segment ${i} has invalid time range`);
   528â†’      }
   529â†’      if (seg.outPoint <= seg.inPoint) {
   530â†’        throw new Error(`Segment ${i} has invalid duration (out <= in)`);
   531â†’      }
   532â†’    }
   533â†’
   534â†’    // Store cut list for building
   535â†’    textEditorState.lastCutList = cutListData.cutList;
   536â†’
   537â†’    // Update state
   538â†’    textEditorState.isDirty = false;
   539â†’
   540â†’    showTextEditorStatus(
   541â†’      `Applied ${applyData.stats.deletions} cuts, ${cutListData.summary.segmentCount} segments`,
   542â†’      'success'
   543â†’    );
   544â†’
   545â†’    // Show build button
   546â†’    updateBuildButtonState(true);
   547â†’
   548â†’  } catch (err) {
   549â†’    console.error('[SPLICE] Text edit apply error:', err);
   550â†’
   551â†’    // Provide user-friendly error messages
   552â†’    let userMessage = err.message;
   553â†’    if (err.message.includes('network') || err.message.includes('timeout') || err.message.includes('fetch')) {
   554â†’      userMessage = 'Network error. Check your connection and try again.';
   555â†’    } else if (err.message.includes('Cut list is empty')) {
   556â†’      userMessage = 'All words were deleted. Nothing left to build.';
   557â†’    } else if (err.message.includes('Backend') || err.message.includes('Invalid cut list')) {
   558â†’      userMessage = 'Server error: ' + err.message;
   559â†’    }
   560â†’
   561â†’    showTextEditorStatus(userMessage, 'error');
   562â†’  } finally {
   563â†’    textEditorState.isProcessing = false;
   564â†’    updateProcessingState(false);
   565â†’  }
   566â†’}
