   595â†’      }
   596â†’    } else {
   597â†’      showTextEditorStatus('Builder not available', 'error');
   598â†’    }
   599â†’  } catch (err) {
   600â†’    console.error('[SPLICE] Build sequence error:', err);
   601â†’    showTextEditorStatus(err.message, 'error');
   602â†’  }
   603â†’}
