  applyEdits: applyTextEdits,
  previewEdits: previewTextEdits,
  undo: undoTextEdit,
  redo: redoTextEdit,
  reset: resetToOriginal,
  search: searchInTranscript,
  buildSequence: buildSequenceFromEdits,
  getState: () => ({ ...textEditorState })
};

