   635→function applyPreset(presetId) {
   636→    const preset = PRESETS[presetId];
   637→    if (!preset) return;
   638→
   639→    // Custom preset - keep current settings
   640→    if (presetId === 'custom' || !preset.settings) {
   641→        saveSettings({ activePreset: 'custom' });
   642→        setStatus('Using custom settings');
   643→        return;
   644→    }
   645→
   646→    const settings = preset.settings;
   647→
   648→    // Apply to UI
   649→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   650→        ui.sensitivitySlider.value = settings.sensitivity;
   651→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   652→    }
   653→
   654→    if (ui.enableJCut) {
   655→        ui.enableJCut.checked = settings.enableJCut || false;
   656→        if (ui.jcutSettings) {
   657→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   658→        }
   659→    }
   660→
   661→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   662→        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   663→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   664→    }
   665→
   666→    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   667→        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   668→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   669→    }
   670→
   671→    // Save merged settings
   672→    saveSettings({
   673→        activePreset: presetId,
   674→        sensitivity: settings.sensitivity,
   675→        threshold: settings.threshold,
   676→        minSilenceLength: settings.minSilenceLength,
   677→        paddingStart: settings.paddingStart,
   678→        paddingEnd: settings.paddingEnd,
   679→        enableTakesDetection: settings.enableTakesDetection,
   680→        enableJCut: settings.enableJCut || false,
   681→        jcutLeadIn: settings.jCutLeadIn || 0.3,
   682→        jcutLeadOut: settings.jCutLeadOut || 0.2
   683→    });
   684→
   685→    setStatus(`Applied ${preset.name} preset`);
   686→}
