   937→
   938→                if (chapterResponse.ok) {
   939→                    const chapterData = await chapterResponse.json();
   940→                    currentChapters = chapterData.chapters || [];
   941→                } else {
   942→                    const errorMsg = await parseErrorResponse(chapterResponse);
