   951→        }
   952→
   953→        // Detect zoom points if enabled
   954→        if (settings.enableZoom) {
   955→            updateProgress(90, 'Detecting zoom points...');
   956→            try {
   957→                const zoomResponse = await fetchWithTimeout(
   958→                    `${getBackendUrl()}/zoom`,
   959→                    {
   960→                        method: 'POST',
