   982→                setStatus('Zoom detection failed - continuing without zoom points', true);
   983→            }
   984→        }
   985→
   986→        // Store results
   987→        lastDetectionResult = {
   988→            silences: previewSilences,
   989→            takes: previewTakes,
   990→            chapters: currentChapters,
   991→            zooms: currentZooms
