  1508→ */
  1509→function announceToScreenReader(message, priority = 'polite') {
  1510→    // Use existing status element or create temporary one
  1511→    const statusEl = document.getElementById('status');
  1512→    if (statusEl && priority === 'polite') {
  1513→        const originalText = statusEl.textContent;
  1514→        statusEl.textContent = message;
  1515→        // Restore after announcement
  1516→        setTimeout(() => {
  1517→            if (statusEl.textContent === message) {
  1518→                statusEl.textContent = originalText;
  1519→            }
  1520→        }, 1000);
  1521→        return;
  1522→    }
  1523→
  1524→    // Create temporary live region for assertive announcements
  1525→    const liveRegion = document.createElement('div');
  1526→    liveRegion.setAttribute('role', 'alert');
  1527→    liveRegion.setAttribute('aria-live', priority);
  1528→    liveRegion.setAttribute('aria-atomic', 'true');
  1529→    liveRegion.className = 'sr-only';
  1530→    liveRegion.style.cssText = 'position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);border:0;';
  1531→    liveRegion.textContent = message;
  1532→    document.body.appendChild(liveRegion);
  1533→
  1534→    // Remove after announcement
  1535→    setTimeout(() => liveRegion.remove(), 1000);
  1536→}
