   876→
   877→  try {
   878→    musicState.isGenerating = true;
   879→    generateBtn.disabled = true;
   880→    generateBtn.textContent = 'Starting...';
   881→
   882→    const options = {
   883→      mood: getSelectedMood(),
   884→      instruments: [getSelectedInstrument()],
