   440→/**
   441→ * Parse error response from backend
   442→ * @param {Response} response - Fetch response
   443→ */
   444→async function parseErrorResponse(response) {
   445→    try {
   446→        const data = await response.json();
   447→        return data.error || data.message || `HTTP ${response.status}`;
   448→    } catch {
   449→        return `HTTP ${response.status}: ${response.statusText}`;
   450→    }
   451→}
