  1090→/**
  1091→ * SECURITY: Escape HTML to prevent XSS attacks
  1092→ * Use this when inserting untrusted content into innerHTML
  1093→ * @param {string} str - String to escape
  1094→ * @returns {string} Escaped string safe for innerHTML
  1095→ */
  1096→function escapeHtml(str) {
  1097→    if (str === null || str === undefined) return '';
  1098→    if (typeof str !== 'string') str = String(str);
  1099→
  1100→    const escapeMap = {
  1101→        '&': '&amp;',
  1102→        '<': '&lt;',
  1103→        '>': '&gt;',
  1104→        '"': '&quot;',
  1105→        "'": '&#x27;',
  1106→        '/': '&#x2F;',
  1107→        '`': '&#x60;'
  1108→    };
  1109→
  1110→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
  1111→}
  1112→
  1113→/**
  1114→ * SECURITY: Create safe HTML from template with escaped values
  1115→ * @param {TemplateStringsArray} strings - Template strings
  1116→ * @param {...any} values - Values to escape
  1117→ * @returns {string} HTML with escaped values
  1118→ */
  1119→function safeHtml(strings, ...values) {
  1120→    return strings.reduce((result, str, i) => {
  1121→        const value = values[i - 1];
  1122→        const escaped = escapeHtml(value);
  1123→        return result + escaped + str;
  1124→    });
  1125→}
