  1127→/**
  1128→ * Format time in MM:SS or HH:MM:SS
  1129→ */
  1130→function formatTime(seconds) {
  1131→    if (isNaN(seconds) || seconds < 0) return '0:00';
  1132→
  1133→    const hours = Math.floor(seconds / 3600);
  1134→    const minutes = Math.floor((seconds % 3600) / 60);
  1135→    const secs = Math.floor(seconds % 60);
  1136→
  1137→    if (hours > 0) {
  1138→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  1139→    }
  1140→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  1141→}
