  1154→/**
  1155→ * Debounce function calls
  1156→ */
  1157→function debounce(func, wait) {
  1158→    let timeout;
  1159→    return function executedFunction(...args) {
  1160→        const later = () => {
  1161→            clearTimeout(timeout);
  1162→            func(...args);
  1163→        };
  1164→        clearTimeout(timeout);
  1165→        timeout = setTimeout(later, wait);
  1166→    };
  1167→}
