  1414→/**
  1415→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1416→ * Character set excludes confusing chars: 0/O, 1/I/L
  1417→ */
  1418→function isValidLicenseKeyFormat(key) {
  1419→    if (!key || typeof key !== 'string') return false;
  1420→    const normalized = key.toUpperCase().trim();
  1421→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1422→    return pattern.test(normalized);
  1423→}
