  1425→/**
  1426→ * Initialize login modal handlers
  1427→ */
  1428→function initLoginModal() {
  1429→    const loginModal = document.getElementById('loginModal');
  1430→    const closeLoginBtn = document.getElementById('closeLoginBtn');
  1431→    const saveLoginBtn = document.getElementById('saveLoginBtn');
  1432→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1433→    const creditBadge = document.getElementById('creditBadge');
  1434→
  1435→    // Handle credit badge click - retry on error, or show login modal
  1436→    if (creditBadge) {
  1437→        creditBadge.addEventListener('click', async () => {
  1438→            if (creditBadge.classList.contains('error')) {
  1439→                creditBadge.textContent = '...';
  1440→                if (typeof refreshCredits === 'function') {
  1441→                    await refreshCredits();
  1442→                }
  1443→                return;
  1444→            }
  1445→            showLoginModal();
  1446→        });
  1447→    }
  1448→
  1449→    // Close login modal
  1450→    if (closeLoginBtn && loginModal) {
  1451→        closeLoginBtn.addEventListener('click', () => {
  1452→            loginModal.classList.add('hidden');
  1453→        });
  1454→    }
  1455→
  1456→    // Close on backdrop click
  1457→    if (loginModal) {
  1458→        loginModal.addEventListener('click', (e) => {
  1459→            if (e.target === loginModal) {
  1460→                loginModal.classList.add('hidden');
  1461→            }
  1462→        });
  1463→    }
  1464→
  1465→    // Activate license key
  1466→    if (saveLoginBtn) {
  1467→        saveLoginBtn.addEventListener('click', async () => {
  1468→            const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
  1469→
  1470→            if (!licenseKey) {
  1471→                showLoginError('Please enter your license key');
  1472→                return;
  1473→            }
  1474→
  1475→            if (!isValidLicenseKeyFormat(licenseKey)) {
  1476→                showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
  1477→                return;
  1478→            }
  1479→
  1480→            saveLoginBtn.disabled = true;
  1481→            saveLoginBtn.textContent = 'Activating...';
  1482→
  1483→            try {
  1484→                // Use /auth/login endpoint which returns JWT tokens
  1485→                const response = await fetchWithTimeout(`${getBackendUrl()}/auth/login`, {
  1486→                    method: 'POST',
  1487→                    headers: { 'Content-Type': 'application/json' },
  1488→                    body: JSON.stringify({ licenseKey: licenseKey })
  1489→                }, SPLICE_CONFIG.FETCH_TIMEOUT);
  1490→
  1491→                const result = await response.json();
  1492→
  1493→                if (!result.success) {
  1494→                    showLoginError(result.error || 'Activation failed');
  1495→                    return;
  1496→                }
  1497→
  1498→                // Store JWT tokens and customer info
  1499→                const expiresInMs = (result.expiresIn || 86400) * 1000; // Default 24h
  1500→                saveSettings({
  1501→                    customerId: result.customerId,
  1502→                    accessToken: result.token,
  1503→                    refreshToken: result.refreshToken,
  1504→                    tokenExpiry: Date.now() + expiresInMs,
  1505→                    licenseKey: licenseKey // Keep for re-authentication
  1506→                });
  1507→
  1508→                loginModal?.classList.add('hidden');
  1509→
  1510→                if (typeof refreshCredits === 'function') {
  1511→                    await refreshCredits();
  1512→                }
  1513→
  1514→                const tierName = result.tier ? result.tier.charAt(0).toUpperCase() + result.tier.slice(1) : 'Unknown';
  1515→                setStatus(`License activated! ${tierName} tier`);
  1516→            } catch (err) {
  1517→                console.error('[SPLICE] License activation error:', err);
  1518→
  1519→                // DIAGNOSTIC LOGGING: Capture exact error details (debug mode only)
  1520→                debugLog('=== CEP LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
  1521→                debugLog('Error name:', err?.name);
  1522→                debugLog('Error message:', err?.message);
  1523→                debugLog('Error stack:', err?.stack);
  1524→                debugLog('Backend URL:', getBackendUrl());
  1525→                debugLog('Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
  1526→                debugLog('Is CEP environment:', typeof CSInterface !== 'undefined');
  1527→                debugLog('User agent:', navigator?.userAgent);
  1528→                debugLog('Error constructor:', err?.constructor?.name);
  1529→                debugLog('Is TypeError:', err?.name === 'TypeError');
  1530→                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
  1531→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1532→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1533→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1534→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1535→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1536→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1537→                debugLog('================================================');
  1538→
  1539→                // Provide detailed error message based on error type
  1540→                let errorMessage = 'Connection error. ';
  1541→
  1542→                if (err.message === 'Request timed out') {
  1543→                    errorMessage += 'Server is not responding. Check if backend is running.';
  1544→                } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
  1545→                    errorMessage += `Cannot reach ${getBackendUrl()}. Check network/firewall.`;
  1546→                } else if (err.message?.includes('CORS')) {
  1547→                    errorMessage += 'CORS error. Contact support.';
  1548→                } else if (err.message?.includes('SSL') || err.message?.includes('certificate')) {
  1549→                    errorMessage += 'SSL certificate error. Try updating your system certificates.';
  1550→                } else if (err.message) {
  1551→                    errorMessage += err.message;
  1552→                } else {
  1553→                    errorMessage += 'Unknown error. Check console for details.';
  1554→                }
  1555→
  1556→                debugLog('Final error message shown to user:', errorMessage);
  1557→                showLoginError(errorMessage);
  1558→            } finally {
  1559→                saveLoginBtn.disabled = false;
  1560→                saveLoginBtn.textContent = 'Activate';
  1561→            }
  1562→        });
  1563→    }
  1564→
  1565→    // Email lookup
  1566→    const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  1567→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1568→
  1569→    if (lookupLicenseBtn) {
  1570→        lookupLicenseBtn.addEventListener('click', async () => {
  1571→            const email = lookupEmailInput?.value?.trim()?.toLowerCase();
  1572→
  1573→            if (!email) {
  1574→                showLoginError('Please enter your email address');
  1575→                return;
  1576→            }
  1577→
  1578→            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1579→            if (!emailRegex.test(email)) {
  1580→                showLoginError('Please enter a valid email address');
  1581→                return;
  1582→            }
  1583→
  1584→            lookupLicenseBtn.disabled = true;
  1585→            lookupLicenseBtn.textContent = 'Looking up...';
  1586→
  1587→            try {
  1588→                const response = await fetchWithTimeout(`${getBackendUrl()}/license/lookup`, {
  1589→                    method: 'POST',
  1590→                    headers: { 'Content-Type': 'application/json' },
  1591→                    body: JSON.stringify({ email })
  1592→                }, 30000);
  1593→
  1594→                const result = await response.json();
  1595→
  1596→                // Note: For security, the backend does NOT return the license key directly.
  1597→                // It sends the key via email to prevent enumeration attacks.
  1598→                if (result.success) {
  1599→                    setStatus(result.message || 'If a license exists, it will be sent to your email.');
  1600→                } else {
  1601→                    showLoginError(result.error || 'Lookup failed. Please try again.');
  1602→                }
  1603→            } catch (err) {
  1604→                console.error('[SPLICE] License lookup error:', err);
  1605→                showLoginError('Failed to look up license. Please try again.');
  1606→            } finally {
  1607→                lookupLicenseBtn.disabled = false;
  1608→                lookupLicenseBtn.textContent = 'Lookup';
  1609→            }
  1610→        });
  1611→    }
  1612→
  1613→    // Handle external URL buttons
  1614→    // CEP panels cannot use target="_blank" for security reasons.
