   966→                            preset: settings.zoomPreset || 'medium',
   967→                            placement: settings.zoomPlacement || 'sentence_start'
   968→                        })
   969→                    }
   970→                );
   971→
   972→                if (zoomResponse.ok) {
   973→                    const zoomData = await zoomResponse.json();
