   997→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   998→
   999→    } catch (error) {
  1000→        console.error('[SPLICE] Detection error:', error);
  1001→        setStatus('Detection failed: ' + error.message, true);
  1002→        hideProgress();
  1003→    } finally {
