   789→function deleteClipsAtSilencePointsInTrack(silences, trackType, trackIndex) {
   790→    try {
   791→        var seq = app.project.activeSequence;
   792→        if (!seq) return JSON.stringify({ error: "No active sequence" });
   793→
   794→        // Validate trackType
   795→        if (trackType !== "video" && trackType !== "audio") {
   796→            return JSON.stringify({ error: "Invalid track type: " + trackType });
   797→        }
   798→
   799→        // Parse silences JSON
   800→        var silencesArr;
   801→        try {
   802→            silencesArr = JSON.parse(silences);
   803→        } catch (parseErr) {
   804→            return JSON.stringify({ error: "Invalid silences JSON: " + parseErr.message });
   805→        }
   806→
   807→        // Get tracks and validate index
   808→        var tracks = trackType === "video" ? seq.videoTracks : seq.audioTracks;
   809→        if (trackIndex < 0 || trackIndex >= tracks.numTracks) {
   810→            return JSON.stringify({ error: "Invalid track index: " + trackIndex + " (max: " + (tracks.numTracks - 1) + ")" });
   811→        }
   812→
   813→        var clips = tracks[trackIndex].clips;
   814→        var deleted = 0;
   815→
   816→        // Iterate backwards to avoid index shifting (use numItems, not length)
   817→        for (var i = clips.numItems - 1; i >= 0; i--) {
   818→            var clip = clips[i];
   819→            var start = clip.start.seconds;
   820→            var end = clip.end.seconds;
   821→            var center = (start + end) / 2;
   822→
   823→            for (var j = 0; j < silencesArr.length; j++) {
   824→                var silence = silencesArr[j];
   825→                var silenceCenter = (silence[0] + silence[1]) / 2;
   826→                if (silenceCenter > start && silenceCenter < end) {
   827→                    clip.remove(false, false);
   828→                    deleted++;
   829→                    break;
   830→                }
   831→            }
   832→        }
   833→
   834→        $.writeln('[SPLICE] deleteClipsAtSilencePointsInTrack: Deleted ' + deleted + ' clips from ' + trackType + ' track ' + trackIndex);
   835→        return JSON.stringify({ deleted: deleted });
   836→    } catch (e) {
   837→        $.writeln('[SPLICE] deleteClipsAtSilencePointsInTrack error: ' + e.message);
   838→        return JSON.stringify({ error: "deleteClipsAtSilencePointsInTrack failed: " + e.message });
   839→    }
   840→}
