   842→function makeRemainingClipsContiguousInTrack(silences, deltas, trackType, trackIndex) {
   843→    silences = JSON.parse(silences);
   844→    deltas = JSON.parse(deltas);
   845→
   846→    var clips;
   847→    if (trackType == "video") {
   848→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   849→    } else if (trackType == "audio") {
   850→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   851→    }
   852→
   853→    for (var i = 0; i < clips.length; i++) {
   854→        var clip = clips[i];
   855→        var end = clip.end.seconds;
   856→        var num_silences = -1;
   857→
   858→        for (var j = 0; j < silences.length; j++) {
   859→            if (end > silences[j][1]) {
   860→                num_silences++;
   861→            }
   862→        }
   863→
   864→        if (num_silences > -1 && num_silences < deltas.length) {
   865→            var delta = deltas[num_silences];
   866→            var t_start = clip.start;
   867→            t_start.seconds = t_start.seconds - delta;
   868→            clip.start = t_start;
   869→            var t_end = clip.end;
   870→            t_end.seconds = t_end.seconds - delta;
   871→            clip.end = t_end;
   872→        }
   873→    }
   874→    return JSON.stringify({ success: true });
   875→}
