  1357→// ============================================================================
  1358→// SEQUENCE BUILDING (CORE v3.5 FUNCTIONALITY)
  1359→// ============================================================================
  1360→
  1361→function buildSequenceFromCutList(cutListJson) {
  1362→    try {
  1363→        var cutList = JSON.parse(cutListJson);
  1364→        var seq = app.project.activeSequence;
  1365→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1366→
  1367→        // Clone the sequence
  1368→        var newSequenceName = seq.name + "_SPLICE";
  1369→        seq.clone();
  1370→
  1371→        // Find the cloned sequence
  1372→        var newSeq = null;
  1373→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
  1374→            var s = app.project.sequences[i];
  1375→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
  1376→                newSeq = s;
  1377→                break;
  1378→            }
  1379→        }
  1380→
  1381→        if (!newSeq) {
  1382→            return JSON.stringify({ error: "Failed to clone sequence" });
  1383→        }
  1384→
  1385→        newSeq.name = newSequenceName;
  1386→        app.project.activeSequence = newSeq;
  1387→
  1388→        // Clear all clips from the new sequence
  1389→        clearSequence();
  1390→
  1391→        // Insert segments
  1392→        var currentPosition = 0;
  1393→        var stats = {
  1394→            segmentsInserted: 0,
  1395→            totalDuration: 0
  1396→        };
  1397→
  1398→        for (var i = 0; i < cutList.segments.length; i++) {
  1399→            var segment = cutList.segments[i];
  1400→
  1401→            // Find the source project item
  1402→            var sourceItem = null;
  1403→            if (segment.sourcePath) {
  1404→                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1405→            }
  1406→            if (!sourceItem && segment.sourceName) {
  1407→                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1408→            }
  1409→
  1410→            if (!sourceItem) continue;
  1411→
  1412→            // Set color label
  1413→            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1414→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1415→            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1416→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1417→            }
  1418→
  1419→            // Insert clip at current position
  1420→            var targetTime = new Time();
  1421→            targetTime.seconds = currentPosition;
  1422→
  1423→            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1424→
  1425→            // Set in/out points
  1426→            var clipDuration = segment.outPoint - segment.inPoint;
  1427→            var videoClips = newSeq.videoTracks[0].clips;
  1428→            var insertedClip = videoClips[videoClips.numItems - 1];
  1429→
  1430→            if (insertedClip) {
  1431→                var inTime = new Time();
  1432→                inTime.seconds = segment.inPoint;
  1433→                insertedClip.inPoint = inTime;
  1434→
  1435→                var outTime = new Time();
  1436→                outTime.seconds = segment.outPoint;
  1437→                insertedClip.outPoint = outTime;
  1438→
  1439→                // Set clip name if takeLabel provided
  1440→                if (segment.takeLabel) {
  1441→                    insertedClip.name = segment.takeLabel;
  1442→                }
  1443→            }
  1444→
  1445→            currentPosition += clipDuration;
  1446→            stats.segmentsInserted++;
  1447→            stats.totalDuration += clipDuration;
  1448→        }
  1449→
  1450→        return JSON.stringify({
  1451→            success: true,
  1452→            sequenceName: newSequenceName,
  1453→            stats: stats
  1454→        });
  1455→    } catch (e) {
  1456→        return JSON.stringify({ error: e.message });
  1457→    }
  1458→}
