  1693→            var numClipsInTrack = qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).numItems;
  1694→            for (var j = 0; j < numClipsInTrack; j++) {
  1695→                if (qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1696→                    qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).moveToTrack(nTracksToMove, 0, "0");
  1697→                    return JSON.stringify({ success: true });
  1698→                }
  1699→            }
  1700→        }
  1701→        return JSON.stringify({ error: "Clip not found at specified time" });
  1702→    } catch (e) {
  1703→        return JSON.stringify({ error: "Move clip failed: " + e.message });
  1704→    }
