  1706→
  1707→function linkIdenticalClipsInTracks(videoTrackIndex, audioTrackIndex) {
  1708→    var selected_clips = app.project.activeSequence.getSelection();
  1709→    for (var i = 0; i < selected_clips.length; i++) {
  1710→        selected_clips[i].setSelected(false, false);
