  1712→
  1713→    var video_clips = app.project.activeSequence.videoTracks[videoTrackIndex].clips;
  1714→    var audio_clips = app.project.activeSequence.audioTracks[audioTrackIndex].clips;
  1715→
  1716→    for (var i = 0; i < video_clips.numItems; i++) {
  1717→        var video_clip = video_clips[i];
  1718→        var video_start = video_clip.start.seconds;
  1719→
  1720→        for (var j = 0; j < audio_clips.numItems; j++) {
  1721→            var audio_clip = audio_clips[j];
  1722→            var audio_start = audio_clip.start.seconds;
  1723→
  1724→            if (Math.abs(video_start - audio_start) < time_tolerance) {
  1725→                video_clip.setSelected(true, false);
  1726→                audio_clip.setSelected(true, false);
  1727→                app.project.activeSequence.linkSelection();
  1728→                video_clip.setSelected(false, false);
  1729→                audio_clip.setSelected(false, false);
  1730→                break;
  1731→            }
  1732→        }
  1733→    }
  1734→    return true;
