    71→// ============================================================================
    72→// JSX BRIDGE - Communicate with Premiere Pro
    73→// ============================================================================
    74→
    75→// JSX timeout configuration (FIX: CEP-CRIT-001 - JSX Bridge Timeout Bug)
    76→const JSX_TIMEOUTS = {
    77→    QUICK: 5000,        // 5s  - simple checks (checkSequenceOpen, getVersion)
    78→    DEFAULT: 30000,     // 30s - most operations (getClips, createMarker)
    79→    LONG: 120000,       // 2m  - export operations, large file I/O
    80→    CRITICAL: 300000    // 5m  - complex timeline operations
    81→};
    82→
    83→const jsx = {
    84→    cs: null,
    85→    _initPromise: null,
    86→    _initialized: false,
    87→    _jsxReady: false,
    88→
    89→    /**
    90→     * Initialize the CSInterface with FireCut-style retry logic
    91→     * Returns a promise that resolves when ready (true/false)
    92→     * Does NOT reject - gracefully handles missing CEP environment
    93→     */
    94→    init: function() {
    95→        if (this._initPromise) return this._initPromise;
    96→
    97→        this._initPromise = this._initWithRetry();
    98→        return this._initPromise;
    99→    },
   100→
   101→    /**
   102→     * FireCut-style retry logic for CSInterface initialization
   103→     * Retries up to 5 times with exponential backoff
   104→     */
   105→    _initWithRetry: async function() {
   106→        const maxRetries = 5;
   107→        const baseDelay = 200; // Start with 200ms, will double each retry
   108→
   109→        for (let attempt = 1; attempt <= maxRetries; attempt++) {
   110→            const success = await this._tryInit(attempt);
   111→            if (success) {
   112→                // After CSInterface init, verify JSX is ready
   113→                const jsxReady = await this._verifyJSXReady();
   114→                if (jsxReady) {
   115→                    this._jsxReady = true;
   116→                    debugLog(`[JSX] Full initialization complete on attempt ${attempt}`);
   117→                    return true;
   118→                } else {
   119→                    debugLog(`[JSX] CSInterface ready but JSX not responding, attempt ${attempt}/${maxRetries}`);
   120→                }
   121→            }
   122→
   123→            if (attempt < maxRetries) {
   124→                const delay = baseDelay * Math.pow(2, attempt - 1);
   125→                debugLog(`[JSX] Init attempt ${attempt} failed, retrying in ${delay}ms...`);
   126→                await this._delay(delay);
   127→            }
   128→        }
   129→
   130→        console.warn('[JSX] Initialization failed after all retries - panel may have limited functionality');
   131→        return this._initialized;
   132→    },
   133→
   134→    /**
   135→     * Single initialization attempt
   136→     */
   137→    _tryInit: function(attempt) {
   138→        return new Promise((resolve) => {
   139→            try {
   140→                if (typeof CSInterface !== 'undefined') {
   141→                    this.cs = new CSInterface();
   142→                    // Verify we're in a real CEP environment by checking for __adobe_cep__
   143→                    if (window.__adobe_cep__) {
   144→                        this._initialized = true;
   145→                        debugLog(`[JSX] CSInterface initialized successfully (CEP environment detected) - attempt ${attempt}`);
   146→                        resolve(true);
   147→                    } else {
   148→                        // CSInterface exists but no CEP runtime - browser/dev mode
   149→                        this._initialized = true;
   150→                        debugLog(`[JSX] CSInterface initialized (development/browser mode) - attempt ${attempt}`);
   151→                        resolve(true);
   152→                    }
   153→                } else {
   154→                    debugLog(`[JSX] CSInterface not yet available - attempt ${attempt}`);
   155→                    resolve(false);
   156→                }
   157→            } catch (err) {
   158→                console.error(`[JSX] Init attempt ${attempt} error:`, err);
   159→                resolve(false);
   160→            }
   161→        });
   162→    },
   163→
   164→    /**
   165→     * FireCut-style JSX readiness verification
   166→     * Tests that hostScript.jsx is actually loaded and responding
   167→     */
   168→    _verifyJSXReady: async function() {
   169→        if (!this.cs) return false;
   170→
   171→        return new Promise((resolve) => {
   172→            const timeout = setTimeout(() => {
   173→                debugLog('[JSX] JSX readiness check timed out');
   174→                resolve(false);
   175→            }, 2000);
   176→
   177→            try {
   178→                // Simple test that hostScript.jsx is loaded and responding
   179→                this.cs.evalScript('(function() { return typeof getActiveSequence === "function" ? "ready" : "not_ready"; })()', (result) => {
   180→                    clearTimeout(timeout);
   181→                    if (result === 'ready') {
   182→                        debugLog('[JSX] JSX bridge verified ready - hostScript.jsx loaded');
   183→                        resolve(true);
   184→                    } else {
   185→                        debugLog('[JSX] JSX functions not yet available:', result);
   186→                        resolve(false);
   187→                    }
   188→                });
   189→            } catch (err) {
   190→                clearTimeout(timeout);
   191→                debugLog('[JSX] JSX readiness check error:', err);
   192→                resolve(false);
   193→            }
   194→        });
   195→    },
   196→
   197→    /**
   198→     * Helper for async delay
   199→     */
   200→    _delay: function(ms) {
   201→        return new Promise(resolve => setTimeout(resolve, ms));
   202→    },
   203→
   204→    /**
   205→     * Ensure JSX is initialized before making calls
   206→     */
   207→    ensureInitialized: async function() {
   208→        if (!this._initialized) {
   209→            await this.init();
   210→        }
   211→        if (!this.cs) {
   212→            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
   213→        }
   214→    },
   215→
   216→    /**
   217→     * Execute ExtendScript in Premiere Pro with timeout protection
   218→     * FIX: CEP-CRIT-001 - JSX Bridge Timeout Bug
   219→     * @param {string} script - ExtendScript to execute
   220→     * @param {number} timeout - Timeout in ms (default: JSX_TIMEOUTS.DEFAULT)
   221→     * @returns {Promise<any>} - Parsed result
   222→     */
   223→    evalScript: async function(script, timeout = JSX_TIMEOUTS.DEFAULT) {
   224→        await this.ensureInitialized();
   225→
   226→        return new Promise((resolve, reject) => {
   227→            let completed = false;
   228→
   229→            // Timeout handler - rejects if ExtendScript hangs
   230→            const timeoutId = setTimeout(() => {
   231→                if (!completed) {
   232→                    completed = true;
   233→                    const scriptPreview = script.length > 100 ? script.substring(0, 100) + '...' : script;
   234→                    const error = new Error(`ExtendScript timeout after ${timeout}ms: ${scriptPreview}`);
   235→                    error.code = 'JSX_TIMEOUT';
   236→                    console.error('[JSX] Timeout:', scriptPreview);
   237→                    reject(error);
   238→                }
   239→            }, timeout);
   240→
   241→            this.cs.evalScript(script, (result) => {
   242→                // Ignore late callbacks after timeout
   243→                if (completed) {
   244→                    console.warn('[JSX] Late evalScript result received after timeout - ignoring');
   245→                    return;
   246→                }
   247→
   248→                completed = true;
   249→                clearTimeout(timeoutId);
   250→
   251→                if (result === 'undefined' || result === undefined) {
   252→                    resolve(null);
   253→                    return;
   254→                }
   255→
   256→                // Check for EvalScript error or ExtendScript runtime errors
   257→                if (typeof result === 'string' && (
   258→                    result.indexOf('EvalScript error') !== -1 ||
   259→                    result.indexOf('Error:') !== -1 ||
   260→                    result.indexOf('TypeError:') !== -1 ||
   261→                    result.indexOf('ReferenceError:') !== -1
   262→                )) {
   263→                    reject(new Error(result));
   264→                    return;
   265→                }
   266→
   267→                // Try to parse JSON
   268→                try {
   269→                    const parsed = JSON.parse(result);
   270→                    if (parsed.error) {
   271→                        reject(new Error(parsed.error));
   272→                    } else {
   273→                        resolve(parsed);
   274→                    }
   275→                } catch (e) {
   276→                    // Return as-is if not JSON
   277→                    resolve(result);
   278→                }
   279→            });
   280→        });
   281→    },
   282→
   283→    /**
   284→     * Call a JSX function with arguments
   285→     * @param {string} funcName - Function name
   286→     * @param {...any} args - Arguments to pass (last arg can be {timeout: number})
   287→     * @returns {Promise<any>}
   288→     */
   289→    call: async function(funcName, ...args) {
   290→        await this.ensureInitialized();
   291→
   292→        // Check if last argument is an options object with timeout
   293→        let timeout = JSX_TIMEOUTS.DEFAULT;
   294→        let funcArgs = args;
   295→
   296→        if (args.length > 0 && typeof args[args.length - 1] === 'object' && args[args.length - 1] !== null) {
   297→            const lastArg = args[args.length - 1];
   298→            if ('_jsxTimeout' in lastArg) {
   299→                timeout = lastArg._jsxTimeout;
   300→                funcArgs = args.slice(0, -1);
   301→            }
   302→        }
   303→
   304→        const escapedArgs = funcArgs.map(arg => {
   305→            if (arg === null || arg === undefined) return 'null';
   306→            if (typeof arg === 'string') {
   307→                // Escape backslashes first, then other special chars
   308→                return `'${arg
   309→                    .replace(/\\/g, '\\\\')
   310→                    .replace(/'/g, "\\'")
   311→                    .replace(/\r/g, '\\r')
   312→                    .replace(/\n/g, '\\n')
   313→                    .replace(/\t/g, '\\t')}'`;
   314→            }
   315→            if (typeof arg === 'object') {
   316→                return `'${JSON.stringify(arg)
   317→                    .replace(/\\/g, '\\\\')
   318→                    .replace(/'/g, "\\'")}'`;
   319→            }
   320→            return String(arg);
   321→        });
   322→
   323→        const script = `${funcName}(${escapedArgs.join(', ')})`;
   324→        return this.evalScript(script, timeout);
   325→    },
   326→
   327→    /**
   328→     * Call a JSX function with explicit timeout
   329→     * Convenience wrapper for operations that need non-default timeouts
   330→     * @param {string} funcName - Function name
   331→     * @param {Array} args - Arguments array
   332→     * @param {number} timeout - Timeout in ms
   333→     * @returns {Promise<any>}
   334→     */
   335→    callWithTimeout: async function(funcName, args = [], timeout = JSX_TIMEOUTS.DEFAULT) {
   336→        return this.call(funcName, ...args, { _jsxTimeout: timeout });
   337→    }
   338→};
   339→
   340→// ============================================================================
   341→// FETCH UTILITIES
   342→// ============================================================================
   343→
   344→/**
   345→ * Fetch with retry logic and exponential backoff
   346→ * @param {string} url - URL to fetch
   347→ * @param {object} options - Fetch options
   348→ * @param {number} timeout - Timeout per attempt (ms)
   349→ * @param {number} maxAttempts - Maximum retry attempts
   350→ * @returns {Promise<Response>}
   351→ */
   352→async function fetchWithRetry(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT, maxAttempts = SPLICE_CONFIG.RETRY_ATTEMPTS) {
   353→    let lastError = null;
   354→
   355→    for (let attempt = 1; attempt <= maxAttempts; attempt++) {
   356→        try {
   357→            debugLog(`[Fetch] Attempt ${attempt}/${maxAttempts}: ${url}`);
   358→            const response = await fetchWithTimeout(url, options, timeout);
   359→
   360→            // Success - return response (don't retry on client errors 4xx)
   361→            if (response.ok || response.status < 500) {
   362→                return response;
   363→            }
   364→
   365→            // Server error (5xx) - retry
   366→            lastError = new Error(`Server error: ${response.status} ${response.statusText}`);
   367→            debugLog(`[Fetch] Server error on attempt ${attempt}, will retry`);
   368→
   369→        } catch (error) {
   370→            lastError = error;
   371→            debugLog(`[Fetch] Network error on attempt ${attempt}: ${error.message}`);
   372→        }
   373→
   374→        // Wait before retry (exponential backoff: 1s, 2s, 4s)
   375→        if (attempt < maxAttempts) {
   376→            const delay = SPLICE_CONFIG.RETRY_DELAY * Math.pow(2, attempt - 1);
   377→            debugLog(`[Fetch] Waiting ${delay}ms before retry...`);
   378→            await new Promise(resolve => setTimeout(resolve, delay));
   379→        }
   380→    }
   381→
   382→    // All attempts failed
   383→    throw lastError || new Error('All retry attempts failed');
   384→}
   385→
   386→/**
   387→ * Fetch with timeout support
   388→ * @param {string} url - URL to fetch
   389→ * @param {object} options - Fetch options
   390→ * @param {number} timeout - Timeout in ms
   391→ */
   392→async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   393→    // DIAGNOSTIC: Log fetch request details (debug mode only)
   394→    debugFetch('=== FETCH REQUEST START ===');
   395→    debugFetch('URL:', url);
   396→    debugFetch('Method:', options?.method || 'GET');
   397→    debugFetch('Headers:', options?.headers);
   398→    debugFetch('Timeout:', timeout);
   399→    debugFetch('Is CEP environment:', typeof CSInterface !== 'undefined');
   400→    debugFetch('=================================');
   401→
   402→    const controller = new AbortController();
   403→    const timeoutId = setTimeout(() => controller.abort(), timeout);
   404→
   405→    try {
   406→        const response = await fetch(url, {
   407→            ...options,
   408→            signal: controller.signal
   409→        });
   410→        clearTimeout(timeoutId);
   411→
   412→        // DIAGNOSTIC: Log successful response (debug mode only)
   413→        debugFetch('=== FETCH RESPONSE SUCCESS ===');
   414→        debugFetch('Status:', response.status);
   415→        debugFetch('OK:', response.ok);
   416→        debugFetch('=================================');
   417→
   418→        return response;
   419→    } catch (error) {
   420→        clearTimeout(timeoutId);
   421→
   422→        // DIAGNOSTIC: Log raw fetch error (debug mode only)
   423→        debugFetch('=== FETCH ERROR CAUGHT ===');
   424→        debugFetch('Error name:', error?.name);
   425→        debugFetch('Error message:', error?.message);
   426→        debugFetch('Error type:', typeof error);
   427→        debugFetch('Error constructor:', error?.constructor?.name);
   428→        debugFetch('Is AbortError:', error.name === 'AbortError');
   429→        debugFetch('Is TypeError:', error.name === 'TypeError');
   430→        debugFetch('Full error object:', JSON.stringify(error, Object.getOwnPropertyNames(error)));
   431→        debugFetch('=================================');
   432→
   433→        if (error.name === 'AbortError') {
   434→            throw new Error(`Request timeout after ${timeout}ms`);
   435→        }
   436→        throw error;
   437→    }
   438→}
   439→
   440→/**
   441→ * Parse error response from backend
   442→ * @param {Response} response - Fetch response
   443→ */
   444→async function parseErrorResponse(response) {
   445→    try {
   446→        const data = await response.json();
   447→        return data.error || data.message || `HTTP ${response.status}`;
   448→    } catch {
   449→        return `HTTP ${response.status}: ${response.statusText}`;
   450→    }
   451→}
   452→
   453→/**
   454→ * Get auth headers for API requests
   455→ * Uses JWT Bearer token if available, with legacy fallback
   456→ */
   457→function getAuthHeaders() {
   458→    const headers = { 'Content-Type': 'application/json' };
   459→    const settings = getSettings();
   460→
   461→    // Prefer JWT Bearer token (modern auth)
   462→    if (settings.accessToken) {
   463→        headers['Authorization'] = `Bearer ${settings.accessToken}`;
   464→        return headers;
   465→    }
   466→
   467→    // Legacy fallback for backwards compatibility
   468→    // This will be removed after 2026-04-11
   469→    if (settings.customerId) {
   470→        headers['x-stripe-customer-id'] = settings.customerId;
   471→        console.warn('[SPLICE] Using deprecated x-stripe-customer-id header. Please re-login to upgrade to JWT auth.');
   472→    }
   473→
   474→    return headers;
   475→}
   476→
   477→/**
   478→ * Check if the access token is expired or about to expire
   479→ * @returns {boolean} True if token needs refresh
   480→ */
   481→function isTokenExpired() {
   482→    const settings = getSettings();
   483→    if (!settings.tokenExpiry) return true;
   484→
   485→    // Refresh if less than 5 minutes remaining
   486→    const bufferMs = 5 * 60 * 1000;
   487→    return Date.now() >= (settings.tokenExpiry - bufferMs);
   488→}
   489→
   490→/**
   491→ * Refresh the access token using the refresh token
   492→ * @returns {Promise<boolean>} True if refresh succeeded
   493→ */
   494→async function refreshAccessToken() {
   495→    const settings = getSettings();
   496→
   497→    if (!settings.refreshToken) {
   498→        debugLog('[Auth] No refresh token available');
   499→        return false;
   500→    }
   501→
   502→    try {
   503→        debugLog('[Auth] Refreshing access token...');
   504→
   505→        const response = await fetchWithTimeout(`${getBackendUrl()}/auth/refresh`, {
   506→            method: 'POST',
   507→            headers: { 'Content-Type': 'application/json' },
   508→            body: JSON.stringify({ refreshToken: settings.refreshToken })
   509→        }, 30000);
   510→
   511→        if (!response.ok) {
   512→            const errorMsg = await parseErrorResponse(response);
   513→            console.error('[Auth] Token refresh failed:', errorMsg);
   514→
   515→            // If refresh token is invalid, clear auth and require re-login
   516→            if (response.status === 401) {
   517→                clearAuthTokens();
   518→            }
   519→            return false;
   520→        }
   521→
   522→        const data = await response.json();
   523→
   524→        // Save new access token
   525→        const expiresInMs = (data.expiresIn || 86400) * 1000; // Default 24h
   526→        saveSettings({
   527→            accessToken: data.token,
   528→            tokenExpiry: Date.now() + expiresInMs
   529→        });
   530→
   531→        debugLog('[Auth] Access token refreshed successfully');
   532→        return true;
   533→    } catch (err) {
   534→        console.error('[Auth] Token refresh error:', err);
   535→        return false;
   536→    }
   537→}
   538→
   539→/**
   540→ * Clear all auth tokens (call on logout or auth failure)
   541→ */
   542→function clearAuthTokens() {
   543→    saveSettings({
   544→        accessToken: null,
   545→        refreshToken: null,
   546→        tokenExpiry: null
   547→    });
   548→    debugLog('[Auth] Auth tokens cleared');
   549→}
   550→
   551→/**
   552→ * Ensure we have a valid access token, refreshing if needed
   553→ * @returns {Promise<boolean>} True if we have a valid token
   554→ */
   555→async function ensureValidToken() {
   556→    const settings = getSettings();
   557→
   558→    // No token at all - need to login
   559→    if (!settings.accessToken) {
   560→        return false;
   561→    }
   562→
   563→    // Token not expired - good to go
   564→    if (!isTokenExpired()) {
   565→        return true;
   566→    }
   567→
   568→    // Try to refresh
   569→    return await refreshAccessToken();
   570→}
