   605→
   606→// Current storage version for future migrations
   607→const SETTINGS_STORAGE_VERSION = 2;
   608→
   609→// Fields that contain sensitive data and should be encrypted
   610→const SENSITIVE_FIELDS = ['customerId', 'licenseKey', 'accessToken', 'refreshToken'];
   611→
   612→// ============================================================================
   613→// ENCRYPTION HELPERS FOR SENSITIVE DATA (CEP-CRIT-002 FIX)
   614→// Uses AES-GCM encryption via Web Crypto API for proper security
   615→// ============================================================================
   616→
   617→// Encryption constants
   618→const SPLICE_ENCRYPTION = {
   619→    ALGORITHM: 'AES-GCM',
   620→    KEY_LENGTH: 256,
   621→    IV_LENGTH: 12,
   622→    TAG_LENGTH: 128,
   623→    SALT: 'SPLICE_PANEL_SALT_v1', // Static salt for key derivation
   624→    ITERATIONS: 100000,
   625→    PREFIX: 'ENC:v1:'  // Prefix to identify encrypted values
   626→};
   627→
   628→// Cached encryption key (derived once per session)
   629→let _encryptionKey = null;
   630→
   631→/**
   632→ * Get or derive the encryption key from a machine-specific identifier
   633→ * Uses PBKDF2 to derive a strong key from available identifiers
   634→ * @returns {Promise<CryptoKey>} The derived encryption key
   635→ */
   636→async function getEncryptionKey() {
   637→    if (_encryptionKey) {
   638→        return _encryptionKey;
   639→    }
   640→
   641→    try {
   642→        // Use a combination of identifiers for key derivation
   643→        // In CEP, we use localStorage ID + app version as the key material
   644→        let keyMaterial = localStorage.getItem('splice_device_id');
   645→        if (!keyMaterial) {
   646→            // Generate a random device ID on first use
   647→            keyMaterial = crypto.randomUUID ? crypto.randomUUID() :
   648→                'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
   649→                    const r = Math.random() * 16 | 0;
   650→                    const v = c === 'x' ? r : (r & 0x3 | 0x8);
   651→                    return v.toString(16);
   652→                });
   653→            localStorage.setItem('splice_device_id', keyMaterial);
   654→        }
   655→
   656→        const encoder = new TextEncoder();
   657→        const keyData = encoder.encode(keyMaterial + SPLICE_ENCRYPTION.SALT);
   658→
   659→        // Import the key material for PBKDF2
   660→        const baseKey = await crypto.subtle.importKey(
   661→            'raw',
   662→            keyData,
   663→            'PBKDF2',
   664→            false,
   665→            ['deriveKey']
   666→        );
   667→
   668→        // Derive the actual AES key
   669→        _encryptionKey = await crypto.subtle.deriveKey(
   670→            {
   671→                name: 'PBKDF2',
   672→                salt: encoder.encode(SPLICE_ENCRYPTION.SALT),
   673→                iterations: SPLICE_ENCRYPTION.ITERATIONS,
   674→                hash: 'SHA-256'
   675→            },
   676→            baseKey,
   677→            {
   678→                name: SPLICE_ENCRYPTION.ALGORITHM,
   679→                length: SPLICE_ENCRYPTION.KEY_LENGTH
   680→            },
   681→            false,
   682→            ['encrypt', 'decrypt']
   683→        );
   684→
   685→        return _encryptionKey;
   686→    } catch (e) {
   687→        debugLog('[Config] Key derivation error:', e);
   688→        return null;
   689→    }
   690→}
   691→
   692→/**
   693→ * Encrypt data for secure localStorage storage using AES-GCM
   694→ *
   695→ * @param {any} data - Data to encrypt
   696→ * @returns {Promise<string|null>} Encrypted string with prefix or null on error
   697→ */
   698→async function encryptForStorageAsync(data) {
   699→    try {
   700→        if (data === null || data === undefined) {
   701→            return null;
   702→        }
   703→
   704→        const key = await getEncryptionKey();
   705→        if (!key) {
   706→            // Fallback to legacy encoding if crypto not available
   707→            debugLog('[Config] Crypto not available, using legacy encoding');
   708→            return legacyEncode(data);
   709→        }
   710→
   711→        const encoder = new TextEncoder();
   712→        const jsonStr = JSON.stringify(data);
   713→        const plaintext = encoder.encode(jsonStr);
   714→
   715→        // Generate random IV
   716→        const iv = crypto.getRandomValues(new Uint8Array(SPLICE_ENCRYPTION.IV_LENGTH));
   717→
   718→        // Encrypt
   719→        const ciphertext = await crypto.subtle.encrypt(
   720→            {
   721→                name: SPLICE_ENCRYPTION.ALGORITHM,
   722→                iv: iv,
   723→                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   724→            },
   725→            key,
   726→            plaintext
   727→        );
   728→
   729→        // Combine IV + ciphertext and encode as base64
   730→        const combined = new Uint8Array(iv.length + ciphertext.byteLength);
   731→        combined.set(iv);
   732→        combined.set(new Uint8Array(ciphertext), iv.length);
   733→
   734→        // Convert to base64 with prefix
   735→        const base64 = btoa(String.fromCharCode(...combined));
   736→        return SPLICE_ENCRYPTION.PREFIX + base64;
   737→    } catch (e) {
   738→        debugLog('[Config] Encryption error:', e);
   739→        return legacyEncode(data);
   740→    }
   741→}
   742→
   743→/**
   744→ * Decrypt data from localStorage using AES-GCM
   745→ *
   746→ * @param {string} data - Encrypted string
   747→ * @returns {Promise<any|null>} Decrypted data or null on error
   748→ */
   749→async function decryptFromStorageAsync(data) {
   750→    try {
   751→        if (!data || typeof data !== 'string') {
   752→            return null;
   753→        }
   754→
   755→        // Check if this is the new encrypted format
   756→        if (!data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   757→            // Try legacy decoding for backward compatibility
   758→            return legacyDecode(data);
   759→        }
   760→
   761→        const key = await getEncryptionKey();
   762→        if (!key) {
   763→            debugLog('[Config] Crypto not available for decryption');
   764→            return null;
   765→        }
   766→
   767→        // Remove prefix and decode base64
   768→        const base64Data = data.slice(SPLICE_ENCRYPTION.PREFIX.length);
   769→        const combined = Uint8Array.from(atob(base64Data), c => c.charCodeAt(0));
   770→
   771→        // Extract IV and ciphertext
   772→        const iv = combined.slice(0, SPLICE_ENCRYPTION.IV_LENGTH);
   773→        const ciphertext = combined.slice(SPLICE_ENCRYPTION.IV_LENGTH);
   774→
   775→        // Decrypt
   776→        const plaintext = await crypto.subtle.decrypt(
   777→            {
   778→                name: SPLICE_ENCRYPTION.ALGORITHM,
   779→                iv: iv,
   780→                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   781→            },
   782→            key,
   783→            ciphertext
   784→        );
   785→
   786→        const decoder = new TextDecoder();
   787→        const jsonStr = decoder.decode(plaintext);
   788→        return JSON.parse(jsonStr);
   789→    } catch (e) {
   790→        // Try legacy decoding as fallback
   791→        return legacyDecode(data);
   792→    }
   793→}
   794→
   795→/**
   796→ * Legacy encoding (Base64) for backward compatibility
   797→ * @param {any} data - Data to encode
   798→ * @returns {string|null} Encoded string
   799→ */
   800→function legacyEncode(data) {
   801→    try {
   802→        if (data === null || data === undefined) {
   803→            return null;
   804→        }
   805→        const jsonStr = JSON.stringify(data);
   806→        const encoded = encodeURIComponent(jsonStr);
   807→        return btoa(encoded);
   808→    } catch (e) {
   809→        return null;
   810→    }
   811→}
   812→
   813→/**
   814→ * Legacy decoding (Base64) for backward compatibility
   815→ * @param {string} data - Encoded string
   816→ * @returns {any|null} Decoded data
   817→ */
   818→function legacyDecode(data) {
   819→    try {
   820→        if (!data || typeof data !== 'string') {
   821→            return null;
   822→        }
   823→        // Skip if it has the encryption prefix
   824→        if (data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   825→            return null;
   826→        }
   827→        const decoded = atob(data);
   828→        const jsonStr = decodeURIComponent(decoded);
   829→        return JSON.parse(jsonStr);
   830→    } catch (e) {
   831→        return null;
   832→    }
   833→}
   834→
   835→/**
   836→ * Synchronous wrapper for encryption (starts async operation)
   837→ * For backward compatibility with existing sync code
   838→ * @param {any} data - Data to encode
   839→ * @returns {string|null} Encoded string (legacy format until async completes)
   840→ */
   841→function encryptForStorage(data) {
   842→    // Return legacy encoding synchronously
   843→    // The async version should be preferred for new code
   844→    return legacyEncode(data);
   845→}
   846→
   847→/**
   848→ * Synchronous wrapper for decryption
   849→ * For backward compatibility with existing sync code
   850→ * @param {string} data - Encoded string
   851→ * @returns {any|null} Decoded data
   852→ */
   853→function decryptFromStorage(data) {
   854→    // Handle new encrypted format by returning null (needs async)
   855→    if (data && data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   856→        debugLog('[Config] Encrypted data requires async decryption');
   857→        return null;
   858→    }
   859→    return legacyDecode(data);
   860→}
   861→
   862→/**
   863→ * Check if a value appears to be encrypted
   864→ *
   865→ * @param {any} value - Value to check
   866→ * @returns {boolean} True if value appears to be encrypted
   867→ */
   868→function isEncryptedValue(value) {
   869→    if (!value || typeof value !== 'string') {
   870→        return false;
   871→    }
   872→    // New encryption format
   873→    if (value.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   874→        return true;
   875→    }
   876→    // Unencrypted values typically start with known patterns
   877→    if (value.startsWith('cus_') || value.startsWith('SPLICE-')) {
   878→        return false;
   879→    }
   880→    // Check if it's legacy base64 encoded JSON
   881→    try {
   882→        const decoded = atob(value);
   883→        const jsonStr = decodeURIComponent(decoded);
   884→        JSON.parse(jsonStr);
   885→        return true;
   886→    } catch {
   887→        return false;
   888→    }
   889→}
   890→
   891→/**
   892→ * Encrypt sensitive fields in settings object
   893→ *
   894→ * @param {Object} settings - Settings object with plaintext sensitive fields
   895→ * @returns {Object} Settings with encrypted sensitive fields
   896→ */
   897→function encryptSensitiveFields(settings) {
   898→    const encrypted = { ...settings };
   899→
   900→    for (const field of SENSITIVE_FIELDS) {
   901→        if (encrypted[field] !== null && encrypted[field] !== undefined) {
   902→            // Only encrypt if not already encrypted
   903→            if (!isEncryptedValue(encrypted[field])) {
   904→                encrypted[field] = encryptForStorage(encrypted[field]);
   905→                debugLog(`[Config] Encrypted field: ${field}`);
   906→            }
   907→        }
   908→    }
   909→
   910→    return encrypted;
   911→}
   912→
   913→/**
   914→ * Decrypt sensitive fields in settings object
   915→ *
   916→ * @param {Object} settings - Settings object with encrypted sensitive fields
   917→ * @returns {Object} Settings with decrypted sensitive fields
   918→ */
   919→function decryptSensitiveFields(settings) {
   920→    const decrypted = { ...settings };
   921→
   922→    for (const field of SENSITIVE_FIELDS) {
   923→        if (decrypted[field] !== null && decrypted[field] !== undefined) {
   924→            // Check if it looks encrypted before attempting to decrypt
   925→            if (isEncryptedValue(decrypted[field])) {
   926→                const decryptedValue = decryptFromStorage(decrypted[field]);
   927→                if (decryptedValue !== null) {
   928→                    decrypted[field] = decryptedValue;
   929→                    debugLog(`[Config] Decrypted field: ${field}`);
   930→                }
   931→            }
   932→            // If not encrypted (legacy data), keep the original value
   933→        }
   934→    }
   935→
   936→    return decrypted;
   937→}
   938→
   939→/**
   940→ * Internal save function to avoid recursion during migration
   941→ * @private
   942→ */
   943→function _saveSettingsInternal(settings) {
   944→    try {
   945→        // Encrypt sensitive fields before storing
   946→        const toStore = encryptSensitiveFields(settings);
   947→
   948→        // Add version marker for future migrations
   949→        toStore._storageVersion = SETTINGS_STORAGE_VERSION;
   950→
   951→        localStorage.setItem('spliceSettings', JSON.stringify(toStore));
   952→        return true;
   953→    } catch (e) {
   954→        console.warn('[Config] Error saving settings:', e);
   955→        return false;
   956→    }
   957→}
   958→
   959→/**
   960→ * Get settings from localStorage
   961→ * Handles migration from unencrypted to encrypted storage format
   962→ */
   963→function getSettings() {
   964→    try {
   965→        const stored = localStorage.getItem('spliceSettings');
   966→        if (stored) {
   967→            const parsed = JSON.parse(stored);
   968→
   969→            // Decrypt sensitive fields (handles both encrypted and legacy unencrypted data)
   970→            const decrypted = decryptSensitiveFields(parsed);
   971→
   972→            // Check if migration is needed (no version marker = legacy data)
   973→            if (!parsed._storageVersion) {
   974→                // Migrate legacy data: save with encryption and version marker
   975→                debugLog('[Config] Migrating legacy settings to encrypted format');
   976→                const migratedSettings = { ...DEFAULT_SETTINGS, ...decrypted };
   977→                // Use internal save to avoid infinite recursion
   978→                _saveSettingsInternal(migratedSettings);
   979→            }
   980→
   981→            // Return merged settings without internal fields
   982→            const result = { ...DEFAULT_SETTINGS, ...decrypted };
   983→            delete result._storageVersion;
   984→            return result;
   985→        }
   986→    } catch (e) {
   987→        console.warn('[Config] Error loading settings:', e);
   988→    }
   989→    return { ...DEFAULT_SETTINGS };
   990→}
   991→
   992→/**
   993→ * Save settings to localStorage
   994→ * Encrypts sensitive fields before storing
   995→ */
   996→function saveSettings(settings) {
   997→    try {
   998→        // Get current settings (decrypted) and merge with new settings
   999→        const current = getSettings();
  1000→        const merged = { ...current, ...settings };
  1001→
  1002→        return _saveSettingsInternal(merged);
  1003→    } catch (e) {
  1004→        console.warn('[Config] Error saving settings:', e);
  1005→        return false;
  1006→    }
  1007→}
  1008→
  1009→/**
  1010→ * Clear specific setting
  1011→ */
  1012→function clearSetting(key) {
  1013→    const settings = getSettings();
  1014→    delete settings[key];
  1015→    _saveSettingsInternal(settings);
  1016→}
