  1618→
  1619→/**
  1620→ * Initialize all external link buttons that need CSInterface.openURLInDefaultBrowser()
  1621→ * CEP panels cannot use target="_blank" due to security restrictions
  1622→ */
  1623→function initExternalLinkButtons() {
  1624→    // Check if CSInterface is available
  1625→    if (typeof CSInterface === 'undefined') {
  1626→        console.warn('[SPLICE] CSInterface not available - external links disabled');
