  1634→        console.warn('[SPLICE] Failed to create CSInterface:', e.message);
  1635→        return;
  1636→    }
  1637→
  1638→    // Buy a License button
  1639→    const buyLicenseBtn = document.querySelector('.btn-buy-license');
  1640→
  1641→    if (buyLicenseBtn) {
  1642→        buyLicenseBtn.addEventListener('click', (e) => {
  1643→            e.preventDefault();
