  1648→            buyLicenseBtn.style.backgroundColor = '#28a745';
  1649→
  1650→            try {
  1651→                cs.openURLInDefaultBrowser('https://spliceclips.com/pricing');
  1652→
  1653→                // Reset button after a delay
  1654→                setTimeout(() => {
  1655→                    buyLicenseBtn.textContent = 'Buy License';
  1656→                    buyLicenseBtn.style.backgroundColor = '';
  1657→                }, 2000);
  1658→            } catch (err) {
