  1660→                buyLicenseBtn.textContent = 'Error';
  1661→                buyLicenseBtn.style.backgroundColor = '#dc3545';
  1662→            }
  1663→        });
  1664→    }
  1665→    
  1666→    // View Plans button (in upgrade modal)
  1667→    const viewPlansBtn = document.querySelector('.btn-view-plans');
  1668→    if (viewPlansBtn) {
  1669→        viewPlansBtn.addEventListener('click', (e) => {
  1670→            e.preventDefault();
  1671→            e.stopPropagation();
  1672→            const url = viewPlansBtn.dataset.url || 'https://spliceclips.com/pricing';
  1673→            cs.openURLInDefaultBrowser(url);
  1674→        });
  1675→    }
  1676→    
  1677→    // Referral link button
  1678→    const referralBtn = document.querySelector('.btn-referral');
  1679→    if (referralBtn) {
  1680→        referralBtn.addEventListener('click', (e) => {
  1681→            e.preventDefault();
