  1716→}
  1717→
  1718→/**
  1719→ * Logout - clear all auth data
  1720→ */
  1721→function logout() {
  1722→    // Clear all auth-related settings
  1723→    saveSettings({
  1724→        customerId: null,
  1725→        accessToken: null,
