  1520→function exportSequenceAudio(outputPath, inPoint, outPoint) {
  1521→    try {
  1522→        var seq = app.project.activeSequence;
  1523→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1524→
  1525→        // Use the AME (Adobe Media Encoder) for export
  1526→        var encoder = app.encoder;
  1527→        if (!encoder) {
  1528→            return JSON.stringify({ error: "Media Encoder not available" });
  1529→        }
  1530→
  1531→        // Create export preset for WAV
  1532→        var presetPath = encoder.getDefaultPresetPath();
  1533→
  1534→        // Queue the export
  1535→        encoder.encodeSequence(
  1536→            seq,
  1537→            outputPath,
  1538→            presetPath,
  1539→            0, // WorkAreaType: 0 = entire, 1 = work area
  1540→            false // removeOnCompletion
  1541→        );
  1542→
  1543→        return JSON.stringify({ success: true, outputPath: outputPath });
  1544→    } catch (e) {
  1545→        return JSON.stringify({ error: e.message });
  1546→    }
  1547→}
  1548→
  1549→/**
  1550→ * Export sequence audio for SPLICE analysis
  1551→ * Uses AME to export WAV audio to temp folder
  1552→ * @returns {Object} { success, outputPath } or { error }
  1553→ */
  1554→function exportSequenceAudioForAnalysis() {
  1555→    try {
  1556→        var seq = app.project.activeSequence;
  1557→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1558→
  1559→        // Generate temp file path
  1560→        var tempFolder = Folder.temp.fsName;
  1561→        var timestamp = new Date().getTime();
  1562→        var outputPath = tempFolder + "/splice_audio_" + timestamp + ".wav";
  1563→
  1564→        // Check if we have audio tracks with content
  1565→        var hasAudio = false;
  1566→        for (var i = 0; i < seq.audioTracks.numTracks; i++) {
  1567→            if (seq.audioTracks[i].clips.numItems > 0) {
  1568→                hasAudio = true;
  1569→                break;
  1570→            }
  1571→        }
  1572→
  1573→        if (!hasAudio) {
  1574→            return JSON.stringify({ error: "Sequence has no audio clips" });
  1575→        }
  1576→
  1577→        // Use AME for export if available
  1578→        if (app.encoder && app.encoder.encodeSequence) {
  1579→            // Find audio-only preset or use default
  1580→            var presetPath = null;
  1581→            try {
  1582→                // Try to find WAV preset in common locations
  1583→                var presetFolders = [
  1584→                    Folder.appData.fsName + "/Adobe/Common/AME/15.0/Presets",
  1585→                    Folder.appData.fsName + "/Adobe/Common/AME/14.0/Presets",
  1586→                    app.path + "/MediaIO/systempresets/58534430_4d756c74-6962697400000000/WAV 48kHz.epr"
  1587→                ];
  1588→
  1589→                for (var i = 0; i < presetFolders.length; i++) {
  1590→                    var presetFile = new File(presetFolders[i]);
  1591→                    if (presetFile.exists) {
  1592→                        presetPath = presetFile.fsName;
  1593→                        break;
  1594→                    }
  1595→                }
  1596→
  1597→                // Fall back to default preset
  1598→                if (!presetPath) {
  1599→                    presetPath = app.encoder.getDefaultPresetPath();
  1600→                }
  1601→            } catch (e) {
  1602→                presetPath = app.encoder.getDefaultPresetPath();
  1603→            }
  1604→
  1605→            // Start the encode
  1606→            var success = app.encoder.encodeSequence(
  1607→                seq,
  1608→                outputPath,
  1609→                presetPath,
  1610→                0, // WorkAreaType: 0 = entire sequence
  1611→                false // removeOnCompletion
  1612→            );
  1613→
  1614→            if (success) {
  1615→                return JSON.stringify({ success: true, outputPath: outputPath, method: "ame" });
  1616→            }
  1617→        }
  1618→
  1619→        // Fallback: Try to get the source audio path from first clip
  1620→        return getFirstClipAudioPath();
  1621→
  1622→    } catch (e) {
  1623→        return JSON.stringify({ error: e.message });
  1624→    }
  1625→}
  1626→
  1627→/**
  1628→ * Get the audio/video file path from the first clip in the timeline
  1629→ * Used as fallback when AME export is not available
  1630→ * @returns {Object} { success, path } or { error }
  1631→ */
  1632→function getFirstClipAudioPath() {
  1633→    try {
  1634→        var seq = app.project.activeSequence;
  1635→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1636→
  1637→        // First try audio tracks
  1638→        for (var i = 0; i < seq.audioTracks.numTracks; i++) {
  1639→            var track = seq.audioTracks[i];
  1640→            if (track.clips.numItems > 0) {
