import type { NextConfig } from "next";

const nextConfig: NextConfig = {
  // Use splice-website as the source directory
  ...(process.env.NODE_ENV === 'production' && {
    output: 'standalone',
  }),
};

export default nextConfig;

{
  "buildCommand": "cd splice-website && npm run build",
  "installCommand": "cd splice-website && npm install",
  "framework": "nextjs",
  "outputDirectory": "splice-website/.next",
  "headers": [
    {
      "source": "/downloads/:path*",
      "headers": [
        {
          "key": "Content-Type",
          "value": "application/octet-stream"
        },
        {
          "key": "Content-Disposition",
          "value": "attachment"
        }
      ]
    }
  ],
  "rewrites": [
    {
      "source": "/downloads/:path*",
      "destination": "/downloads/:path*"
    }
  ]
}

