     1â†’/**
     2â†’ * SPLICE Backend Server
     3â†’ *
     4â†’ * Main entry point for the SPLICE backend API.
     5â†’ * Orchestrates the audio analysis pipeline.
     6â†’ *
     7â†’ * Slices:
     8â†’ * - Slice 4: Transcription (services/transcription.js)
     9â†’ * - Slice 5: Take Detection (services/takeDetection.js)
    10â†’ *
    11â†’ * Architecture: Modular routes (see routes/ directory)
    12â†’ */
    13â†’
    14â†’
    15â†’require('dotenv').config();
    16â†’
    17â†’const express = require('express');
    18â†’const cors = require('cors');
    19â†’const helmet = require('helmet');
    20â†’const cookieParser = require('cookie-parser');
    21â†’const fs = require('fs');
    22â†’const https = require('https');
    23â†’const http = require('http');
    24â†’const path = require('path');
    25â†’const crypto = require('crypto');
    26â†’
    27â†’// MEDIUM-005: Request ID Tracing Middleware
    28â†’const { requestIdMiddleware } = require('./middleware/requestId');
    29â†’
    30â†’// HIGH-008: Sentry Error Monitoring Integration
    31â†’let Sentry = null;
    32â†’if (process.env.SENTRY_DSN) {
    33â†’  try {
    34â†’    Sentry = require('@sentry/node');
    35â†’    console.log('[SPLICE] Sentry module loaded, will initialize after app creation');
    36â†’  } catch (_err) {
    37â†’    console.warn('[SPLICE] @sentry/node not installed. Run: npm install @sentry/node');
    38â†’  }
    39â†’}
    40â†’
    41â†’// Check if running in production (Railway injects RAILWAY_ENVIRONMENT)
    42â†’const isProduction = process.env.NODE_ENV === 'production' || process.env.RAILWAY_ENVIRONMENT;
    43â†’
    44â†’// Import services
    45â†’const usageTracking = require('./services/usageTracking');
    46â†’const { requireCredits, requireFeature, ipRateLimit } = require('./middleware/rateLimiter');
    47â†’const { generateToken, generateRefreshToken, verifyToken, authenticateToken, maskSensitiveData, blacklistToken } = require('./middleware/auth');
    48â†’const referralService = require('./services/referralService');
    49â†’const licenseService = require('./services/licenseService');
    50â†’const emailService = require('./services/emailService');
    51â†’const { getSupportedLanguages, getAvailableBleepSounds } = require('./services/profanityDetection');
    52â†’const { getSupportedFormats } = require('./services/captionExporter');
    53â†’
    54â†’// Import route modules
    55â†’const healthRoutes = require('./routes/health');
    56â†’const analyzeRoutes = require('./routes/analyze');
    57â†’const silencesRoutes = require('./routes/silences');
    58â†’const detectionRoutes = require('./routes/detection');
    59â†’const exportRoutes = require('./routes/export');
    60â†’const multitrackRoutes = require('./routes/multitrack');
    61â†’const cutListRoutes = require('./routes/cutList');
    62â†’const zoomRoutes = require('./routes/zoom');
    63â†’const chaptersRoutes = require('./routes/chapters');
    64â†’const youtubeRoutes = require('./routes/youtube');
    65â†’const captionsRoutes = require('./routes/captions');
    66â†’const textEditRoutes = require('./routes/textEdit');
    67â†’const reframeRoutes = require('./routes/reframe');
    68â†’const batchRoutes = require('./routes/batch');
    69â†’const authRoutes = require('./routes/auth');
