     1‚Üí/**
     2‚Üí * SPLICE CEP Host Script
     3‚Üí * ExtendScript for Premiere Pro
     4‚Üí *
     5‚Üí * Provides all Premiere Pro API functions called via jsx.evalScript()
     6‚Üí * Based on proven patterns from FireCut and SPLICE UXP builder
     7‚Üí */
     8‚Üí
     9‚Üí// Include shared utilities (constants, time conversion)
    10‚Üí#include "jsxUtils.jsx"
    11‚Üí
    12‚Üí// ============================================================================
    13‚Üí// RUNTIME STATE (not in jsxUtils because they are mutable)
    14‚Üí// ============================================================================
    15‚Üívar temp_bin = null;
    16‚Üívar sequence_default_timecode = null;
    17‚Üí
    18‚Üí// ============================================================================
    19‚Üí// INITIALIZATION
    20‚Üí// ============================================================================
    21‚Üí
    22‚Üí/**
    23‚Üí * Check if QE (Quality Engine) DOM is available
    24‚Üí * QE provides extended functionality not available in standard DOM
    25‚Üí */
    26‚Üífunction isQEAvailable() {
    27‚Üí    try {
    28‚Üí        return typeof qe !== 'undefined' && qe !== null && qe.project;
    29‚Üí    } catch (e) {
    30‚Üí        return false;
    31‚Üí    }
    32‚Üí}
    33‚Üí
    34‚Üí/**
    35‚Üí * Enable QE DOM if not already enabled
    36‚Üí * @returns {boolean} true if QE is now available
    37‚Üí */
    38‚Üífunction enableQE() {
    39‚Üí    try {
    40‚Üí        if (isQEAvailable()) return true;
    41‚Üí        app.enableQE();
    42‚Üí        return isQEAvailable();
    43‚Üí    } catch (e) {
    44‚Üí        $.writeln('[SPLICE] enableQE failed: ' + e.message);
    45‚Üí        return false;
    46‚Üí    }
    47‚Üí}
    48‚Üí
    49‚Üí/**
    50‚Üí * Safe wrapper for QE operations
    51‚Üí * @param {Function} operation - The QE operation to execute
    52‚Üí * @param {string} operationName - Name for error reporting
    53‚Üí * @returns {Object} Result or error object
    54‚Üí */
    55‚Üífunction safeQEOperation(operation, operationName) {
    56‚Üí    try {
    57‚Üí        if (!isQEAvailable()) {
    58‚Üí            if (!enableQE()) {
    59‚Üí                return { error: "QE not available. Please ensure Premiere Pro supports QE DOM." };
    60‚Üí            }
    61‚Üí        }
    62‚Üí        return operation();
    63‚Üí    } catch (e) {
    64‚Üí        return { error: operationName + " failed: " + e.message };
    65‚Üí    }
    66‚Üí}
    67‚Üí
    68‚Üífunction initialise() {
    69‚Üí    storeDefaultTimecode();
    70‚Üí    // Try to enable QE on startup
    71‚Üí    var qeStatus = enableQE();
    72‚Üí    return JSON.stringify({ success: true, version: SPLICE_VERSION, qeAvailable: qeStatus });
    73‚Üí}
    74‚Üí
    75‚Üífunction storeDefaultTimecode() {
    76‚Üí    try {
    77‚Üí        if (!app || !app.project || !app.project.activeSequence) {
    78‚Üí            $.writeln('[SPLICE] storeDefaultTimecode: No active sequence available');
    79‚Üí            return false;
    80‚Üí        }
    81‚Üí        sequence_default_timecode = app.project.activeSequence.getSettings().videoDisplayFormat;
    82‚Üí        $.writeln('[SPLICE] storeDefaultTimecode: Stored format: ' + sequence_default_timecode);
    83‚Üí        return true;
    84‚Üí    } catch (e) {
    85‚Üí        $.writeln('[SPLICE] storeDefaultTimecode failed: ' + e.message);
    86‚Üí        return false;
    87‚Üí    }
    88‚Üí}
    89‚Üí
    90‚Üífunction checkSequenceOpen() {
    91‚Üí    try {
    92‚Üí        // Quick validation checks without logging every step
    93‚Üí        if (!app || !app.project || !app.project.sequences) {
    94‚Üí            $.writeln('[SPLICE] checkSequenceOpen: ERROR - Premiere Pro app/project not ready');
    95‚Üí            return false;
    96‚Üí        }
    97‚Üí
    98‚Üí        // Primary check: Standard DOM activeSequence
    99‚Üí        if (app.project.activeSequence) {
   100‚Üí            var seq = app.project.activeSequence;
   101‚Üí            if (seq.name && seq.sequenceID) {
   102‚Üí                $.writeln('[SPLICE] checkSequenceOpen: ‚úì Active sequence found: ' + seq.name);
   103‚Üí                return true;
   104‚Üí            }
   105‚Üí        }
   106‚Üí
   107‚Üí        // Fallback: Try QE DOM if standard DOM failed
   108‚Üí        if (enableQE()) {
   109‚Üí            try {
   110‚Üí                var qeSeq = qe.project.getActiveSequence();
   111‚Üí                if (qeSeq && qeSeq.name) {
   112‚Üí                    $.writeln('[SPLICE] checkSequenceOpen: ‚úì Active sequence found via QE: ' + qeSeq.name);
   113‚Üí                    return true;
   114‚Üí                }
   115‚Üí            } catch (qeErr) {
   116‚Üí                // Silent fallback failure - not critical
   117‚Üí            }
   118‚Üí        }
   119‚Üí
   120‚Üí        $.writeln('[SPLICE] checkSequenceOpen: ‚úó No active sequence found');
   121‚Üí        return false;
   122‚Üí    } catch (e) {
   123‚Üí        $.writeln('[SPLICE] checkSequenceOpen: ERROR - ' + e.message);
   124‚Üí        return false;
   125‚Üí    }
   126‚Üí}
   127‚Üí
   128‚Üífunction checkPremiereReady() {
   129‚Üí    try {
   130‚Üí        // Check if core Premiere Pro APIs are available and initialized
   131‚Üí        if (!app || typeof app === 'undefined') {
   132‚Üí            $.writeln('[SPLICE] checkPremiereReady: app object not available');
   133‚Üí            return false;
   134‚Üí        }
   135‚Üí
   136‚Üí        if (!app.project || typeof app.project === 'undefined') {
   137‚Üí            $.writeln('[SPLICE] checkPremiereReady: app.project not available');
   138‚Üí            return false;
   139‚Üí        }
   140‚Üí
   141‚Üí        if (!app.project.sequences || typeof app.project.sequences === 'undefined') {
   142‚Üí            $.writeln('[SPLICE] checkPremiereReady: app.project.sequences not available');
   143‚Üí            return false;
   144‚Üí        }
   145‚Üí
   146‚Üí        $.writeln('[SPLICE] checkPremiereReady: ‚úì Premiere Pro APIs ready');
   147‚Üí        return true;
   148‚Üí    } catch (e) {
   149‚Üí        $.writeln('[SPLICE] checkPremiereReady: ERROR - ' + e.message);
   150‚Üí        return false;
   151‚Üí    }
   152‚Üí}
