   160‚Üí// ============================================================================
   161‚Üí// SEQUENCE & PROJECT OPERATIONS
   162‚Üí// ============================================================================
   163‚Üí
   164‚Üífunction getActiveSequence() {
   165‚Üí    var seq = app.project.activeSequence;
   166‚Üí    if (!seq) return JSON.stringify({ error: "No active sequence" });
   167‚Üí
   168‚Üí    return JSON.stringify({
   169‚Üí        name: seq.name,
   170‚Üí        id: seq.sequenceID,
   171‚Üí        duration: seq.end,
   172‚Üí        videoTrackCount: seq.videoTracks.numTracks,
   173‚Üí        audioTrackCount: seq.audioTracks.numTracks,
   174‚Üí        frameRate: seq.getSettings().videoFrameRate.seconds,
   175‚Üí        width: seq.frameSizeHorizontal,
   176‚Üí        height: seq.frameSizeVertical
   177‚Üí    });
   178‚Üí}
   179‚Üí
   180‚Üífunction getSequenceSettings() {
   181‚Üí    var seq = app.project.activeSequence;
   182‚Üí    if (!seq) return JSON.stringify({ error: "No active sequence" });
   183‚Üí
   184‚Üí    var settings = seq.getSettings();
   185‚Üí    return JSON.stringify({
   186‚Üí        frameRate: settings.videoFrameRate.seconds,
   187‚Üí        width: settings.videoFrameWidth,
   188‚Üí        height: settings.videoFrameHeight,
   189‚Üí        displayFormat: settings.videoDisplayFormat
   190‚Üí    });
   191‚Üí}
   192‚Üí
   193‚Üífunction cloneSequence(newName) {
   194‚Üí    var seq = app.project.activeSequence;
   195‚Üí    if (!seq) return JSON.stringify({ error: "No active sequence" });
   196‚Üí
   197‚Üí    try {
   198‚Üí        // Clone the sequence
   199‚Üí        seq.clone();
   200‚Üí
   201‚Üí        // Find the cloned sequence (last one with matching name pattern)
   202‚Üí        var clonedSeq = null;
   203‚Üí        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
   204‚Üí            var s = app.project.sequences[i];
   205‚Üí            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
   206‚Üí                clonedSeq = s;
   207‚Üí                break;
   208‚Üí            }
   209‚Üí        }
   210‚Üí
   211‚Üí        if (clonedSeq && newName) {
   212‚Üí            clonedSeq.name = newName;
   213‚Üí        }
   214‚Üí
   215‚Üí        return JSON.stringify({
   216‚Üí            success: true,
   217‚Üí            sequenceId: clonedSeq ? clonedSeq.sequenceID : null,
   218‚Üí            name: clonedSeq ? clonedSeq.name : newName
   219‚Üí        });
   220‚Üí    } catch (e) {
   221‚Üí        return JSON.stringify({ error: e.message });
   222‚Üí    }
   223‚Üí}
   224‚Üí
   225‚Üífunction createNewSequence(name) {
   226‚Üí    try {
   227‚Üí        app.project.createNewSequence(name);
   228‚Üí        var seq = app.project.activeSequence;
   229‚Üí        return JSON.stringify({
   230‚Üí            success: true,
   231‚Üí            sequenceId: seq.sequenceID,
   232‚Üí            name: seq.name
   233‚Üí        });
   234‚Üí    } catch (e) {
   235‚Üí        return JSON.stringify({ error: e.message });
   236‚Üí    }
   237‚Üí}
   238‚Üí
   239‚Üífunction setActiveSequence(sequenceId) {
   240‚Üí    for (var i = 0; i < app.project.sequences.numSequences; i++) {
   241‚Üí        if (app.project.sequences[i].sequenceID === sequenceId) {
   242‚Üí            app.project.activeSequence = app.project.sequences[i];
   243‚Üí            return JSON.stringify({ success: true });
   244‚Üí        }
   245‚Üí    }
   246‚Üí    return JSON.stringify({ error: "Sequence not found" });
   247‚Üí}
