   249‚Üí/**
   250‚Üí * Seek playhead to a specific time in seconds
   251‚Üí */
   252‚Üífunction seekToTime(seconds) {
   253‚Üí    var seq = app.project.activeSequence;
   254‚Üí    if (!seq) return JSON.stringify({ error: "No active sequence" });
   255‚Üí
   256‚Üí    try {
   257‚Üí        // Convert seconds to ticks
   258‚Üí        var ticks = seconds * TICKS_PER_SECOND;
   259‚Üí
   260‚Üí        // Create a Time object and set position
   261‚Üí        var playerPos = seq.getPlayerPosition();
   262‚Üí        playerPos.seconds = seconds;
   263‚Üí        seq.setPlayerPosition(playerPos.ticks.toString());
   264‚Üí
   265‚Üí        return JSON.stringify({ success: true, position: seconds });
   266‚Üí    } catch (e) {
   267‚Üí        return JSON.stringify({ error: e.message });
   268‚Üí    }
   269‚Üí}
   270‚Üí
   271‚Üí/**
   272‚Üí * Get current playhead position in seconds
   273‚Üí */
   274‚Üífunction getPlayerPosition() {
   275‚Üí    var seq = app.project.activeSequence;
   276‚Üí    if (!seq) return JSON.stringify({ error: "No active sequence" });
   277‚Üí
   278‚Üí    try {
   279‚Üí        var pos = seq.getPlayerPosition();
   280‚Üí        return JSON.stringify({
   281‚Üí            ticks: pos.ticks,
   282‚Üí            seconds: pos.seconds
   283‚Üí        });
   284‚Üí    } catch (e) {
   285‚Üí        return JSON.stringify({ error: e.message });
   286‚Üí    }
   287‚Üí}
