   289‚Üí// ============================================================================
   290‚Üí// RAZOR OPERATIONS
   291‚Üí// ============================================================================
   292‚Üí
   293‚Üífunction razorSequenceAtFrames(frames) {
   294‚Üí    if (!isQEAvailable() && !enableQE()) {
   295‚Üí        return JSON.stringify({ error: "QE not available for razor operation" });
   296‚Üí    }
   297‚Üí    try {
   298‚Üí        frames = String(frames);
   299‚Üí        setTimecodeToFrames();
   300‚Üí        qe.project.getActiveSequence().razor(frames);
   301‚Üí        return JSON.stringify({ success: true });
   302‚Üí    } catch (e) {
   303‚Üí        return JSON.stringify({ error: "Razor failed: " + e.message });
   304‚Üí    }
   305‚Üí}
   306‚Üí
   307‚Üífunction razorSequenceAtSeconds(seconds) {
   308‚Üí    try {
   309‚Üí        // FIX: Add null check before accessing activeSequence
   310‚Üí        if (!app || !app.project || !app.project.activeSequence) {
   311‚Üí            $.writeln('[SPLICE] razorSequenceAtSeconds: No active sequence');
   312‚Üí            return JSON.stringify({ error: "No active sequence" });
   313‚Üí        }
   314‚Üí
   315‚Üí        var frames = parseInt(Math.round(seconds / app.project.activeSequence.getSettings().videoFrameRate.seconds)).toString();
   316‚Üí        return razorSequenceAtFrames(frames);
   317‚Üí    } catch (e) {
   318‚Üí        $.writeln('[SPLICE] razorSequenceAtSeconds failed: ' + e.message);
   319‚Üí        return JSON.stringify({ error: "Razor at seconds failed: " + e.message });
   320‚Üí    }
   321‚Üí}
   322‚Üí
   323‚Üífunction razorSequenceAtFramesArray(silences_array) {
   324‚Üí    if (!isQEAvailable() && !enableQE()) {
   325‚Üí        return JSON.stringify({ error: "QE not available for razor operation" });
   326‚Üí    }
   327‚Üí    try {
   328‚Üí        setTimecodeToFrames();
   329‚Üí        try {
   330‚Üí            silences_array = JSON.parse(silences_array);
   331‚Üí        } catch (e) {
   332‚Üí            return JSON.stringify({ error: "Invalid JSON input: " + e.message });
   333‚Üí        }
   334‚Üí        var razorCount = 0;
   335‚Üí        for (var i = 0; i < silences_array.length; i++) {
   336‚Üí            for (var j = 0; j < silences_array[i].length; j++) {
   337‚Üí                var frames = String(silences_array[i][j]);
   338‚Üí                qe.project.getActiveSequence().razor(frames);
   339‚Üí                razorCount++;
   340‚Üí            }
   341‚Üí        }
   342‚Üí        return JSON.stringify({ success: true, razorCount: razorCount });
   343‚Üí    } catch (e) {
   344‚Üí        return JSON.stringify({ error: "Razor array failed: " + e.message });
   345‚Üí    }
   346‚Üí}
   347‚Üí
   348‚Üífunction razorTrackAtFrames(trackType, trackIndex, frames) {
   349‚Üí    if (!isQEAvailable() && !enableQE()) {
   350‚Üí        return JSON.stringify({ error: "QE not available for track razor" });
   351‚Üí    }
   352‚Üí    try {
   353‚Üí        frames = String(frames);
   354‚Üí        setTimecodeToFrames();
   355‚Üí        if (trackType == "video") {
   356‚Üí            qe.project.getActiveSequence().getVideoTrackAt(trackIndex).razor(frames);
   357‚Üí        } else if (trackType == "audio") {
   358‚Üí            qe.project.getActiveSequence().getAudioTrackAt(trackIndex).razor(frames);
   359‚Üí        }
   360‚Üí        return JSON.stringify({ success: true });
   361‚Üí    } catch (e) {
   362‚Üí        return JSON.stringify({ error: "Track razor failed: " + e.message });
   363‚Üí    }
   364‚Üí}
   365‚Üí
   366‚Üífunction razorTrackAtSeconds(trackType, trackIndex, seconds) {
   367‚Üí    try {
   368‚Üí        // FIX: Add null check before accessing activeSequence
   369‚Üí        if (!app || !app.project || !app.project.activeSequence) {
   370‚Üí            $.writeln('[SPLICE] razorTrackAtSeconds: No active sequence');
   371‚Üí            return JSON.stringify({ error: "No active sequence" });
   372‚Üí        }
   373‚Üí
   374‚Üí        var frames = parseInt(Math.round(seconds / app.project.activeSequence.getSettings().videoFrameRate.seconds)).toString();
   375‚Üí        return razorTrackAtFrames(trackType, trackIndex, frames);
   376‚Üí    } catch (e) {
   377‚Üí        $.writeln('[SPLICE] razorTrackAtSeconds failed: ' + e.message);
   378‚Üí        return JSON.stringify({ error: "Track razor at seconds failed: " + e.message });
   379‚Üí    }
   380‚Üí}
