   386‚Üífunction getClipsInTrack(trackIndex, trackType) {
   387‚Üí    var clips = [];
   388‚Üí    if (trackType == "audio") {
   389‚Üí        for (var i = 0; i < app.project.activeSequence.audioTracks[trackIndex].clips.numItems; i++) {
   390‚Üí            var clip = app.project.activeSequence.audioTracks[trackIndex].clips[i];
   391‚Üí            clips.push({
   392‚Üí                index: i,
   393‚Üí                name: clip.name,
   394‚Üí                start: clip.start.seconds,
   395‚Üí                end: clip.end.seconds,
   396‚Üí                duration: clip.duration.seconds,
   397‚Üí                inPoint: clip.inPoint.seconds,
   398‚Üí                outPoint: clip.outPoint.seconds,
   399‚Üí                mediaPath: clip.projectItem ? clip.projectItem.getMediaPath() : null
   400‚Üí            });
   401‚Üí        }
   402‚Üí    } else if (trackType == "video") {
   403‚Üí        for (var i = 0; i < app.project.activeSequence.videoTracks[trackIndex].clips.numItems; i++) {
   404‚Üí            var clip = app.project.activeSequence.videoTracks[trackIndex].clips[i];
   405‚Üí            clips.push({
   406‚Üí                index: i,
   407‚Üí                name: clip.name,
   408‚Üí                start: clip.start.seconds,
   409‚Üí                end: clip.end.seconds,
   410‚Üí                duration: clip.duration.seconds,
   411‚Üí                inPoint: clip.inPoint.seconds,
   412‚Üí                outPoint: clip.outPoint.seconds,
   413‚Üí                mediaPath: clip.projectItem ? clip.projectItem.getMediaPath() : null
   414‚Üí            });
   415‚Üí        }
   416‚Üí    }
   417‚Üí    return JSON.stringify(clips);
   418‚Üí}
   419‚Üí
   420‚Üífunction getClipsInTrack_Timings_Seconds(trackIndex, trackType) {
   421‚Üí    var clips = [];
   422‚Üí    if (trackType == "audio") {
   423‚Üí        for (var i = 0; i < app.project.activeSequence.audioTracks[trackIndex].clips.numItems; i++) {
   424‚Üí            clips.push([
   425‚Üí                app.project.activeSequence.audioTracks[trackIndex].clips[i].start.seconds,
   426‚Üí                app.project.activeSequence.audioTracks[trackIndex].clips[i].end.seconds
   427‚Üí            ]);
