   429‚Üí    } else if (trackType == "video") {
   430‚Üí        for (var i = 0; i < app.project.activeSequence.videoTracks[trackIndex].clips.numItems; i++) {
   431‚Üí            clips.push([
   432‚Üí                app.project.activeSequence.videoTracks[trackIndex].clips[i].start.seconds,
   433‚Üí                app.project.activeSequence.videoTracks[trackIndex].clips[i].end.seconds
   434‚Üí            ]);
   435‚Üí        }
